import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useCart } from '../context/CartContext';

const CartPage = () => {
  const { cartItems, removeFromCart, updateQuantity, getCartTotal, clearCart } = useCart();
  const navigate = useNavigate();
  const [removingItems, setRemovingItems] = useState([]);
  const [showClearConfirm, setShowClearConfirm] = useState(false);

  // Handle remove with animation
  const handleRemove = (itemId) => {
    setRemovingItems([...removingItems, itemId]);
    setTimeout(() => {
      removeFromCart(itemId);
      setRemovingItems(removingItems.filter(id => id !== itemId));
    }, 300);
  };

  // Handle clear cart
  const handleClearCart = () => {
    clearCart();
    setShowClearConfirm(false);
  };

  // Safe check for cart
  if (!cartItems || cartItems.length === 0) {
    return (
      <motion.div 
        className="empty-cart-page"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        <div className="empty-cart-content">
          <motion.div
            className="empty-cart-icon"
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ type: 'spring', stiffness: 200, damping: 15 }}
          >
            üõí
          </motion.div>
          <motion.h2
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            Your Cart is Empty
          </motion.h2>
          <motion.p
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.3 }}
          >
            Looks like you haven't added anything to your cart yet.
          </motion.p>
          <Link to="/">
            <motion.button
              className="continue-shopping-btn"
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4 }}
              whileHover={{ scale: 1.05, boxShadow: '0 10px 40px rgba(102, 126, 234, 0.3)' }}
              whileTap={{ scale: 0.95 }}
            >
              <span>üõçÔ∏è</span>
              <span>Continue Shopping</span>
            </motion.button>
          </Link>
        </div>
      </motion.div>
    );
  }

  const subtotal = getCartTotal();
  const shipping = subtotal > 50 ? 0 : 5.99;
  const tax = subtotal * 0.08;
  const total = subtotal + shipping + tax;
  const discount = subtotal > 100 ? subtotal * 0.1 : 0;
  const finalTotal = total - discount;

  return (
    <motion.div 
      className="cart-page"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
    >
      <div className="cart-container">
        {/* Header */}
        <motion.div 
          className="cart-header"
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
        >
          <div>
            <h1>Shopping Cart</h1>
            <p className="cart-count">
              {cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}
            </p>
          </div>
          <motion.button
            className="clear-cart-btn"
            onClick={() => setShowClearConfirm(true)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <span>üóëÔ∏è</span>
            <span>Clear Cart</span>
          </motion.button>
        </motion.div>

        {/* Progress Bar */}
        <motion.div 
          className="free-shipping-progress"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          {subtotal < 50 ? (
            <>
              <div className="progress-bar">
                <motion.div 
                  className="progress-fill"
                  initial={{ width: 0 }}
                  animate={{ width: `${(subtotal / 50) * 100}%` }}
                  transition={{ duration: 0.8, ease: 'easeOut' }}
                />
              </div>
              <p>
                Add <strong>${(50 - subtotal).toFixed(2)}</strong> more to get FREE shipping! üöö
              </p>
            </>
          ) : (
            <p className="free-shipping-achieved">
              üéâ Congratulations! You've qualified for FREE shipping!
            </p>
          )}
        </motion.div>

        <div className="cart-content">
          {/* Cart Items */}
          <div className="cart-items-section">
            <AnimatePresence mode="popLayout">
              {cartItems.map((item, index) => (
                <motion.div
                  key={item.id}
                  className={`cart-item ${removingItems.includes(item.id) ? 'removing' : ''}`}
                  layout
                  initial={{ opacity: 0, x: -50 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 100, height: 0 }}
                  transition={{ 
                    layout: { duration: 0.3 },
                    opacity: { duration: 0.2 },
                    delay: index * 0.05 
                  }}
                  whileHover={{ 
                    scale: 1.02,
                    boxShadow: '0 8px 30px rgba(0,0,0,0.12)',
                    transition: { duration: 0.2 }
                  }}
                >
                  <div className="item-image-wrapper">
                    <motion.img 
                      src={item.image} 
                      alt={item.name}
                      whileHover={{ scale: 1.1 }}
                      transition={{ duration: 0.3 }}
                    />
                  </div>

                  <div className="item-details">
                    <h3>{item.name}</h3>
                    <p className="item-category">{item.category}</p>
                    <div className="item-price-mobile">${item.price}</div>
                  </div>

                  <div className="item-price-desktop">
                    <span>${item.price.toFixed(2)}</span>
                  </div>

                  <div className="item-quantity-controls">
                    <motion.button
                      onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      whileHover={{ scale: 1.2 }}
                      whileTap={{ scale: 0.9 }}
                      disabled={item.quantity <= 1}
                    >
                      ‚àí
                    </motion.button>
                    <motion.span 
                      key={item.quantity}
                      initial={{ scale: 1.3, opacity: 0.5 }}
                      animate={{ scale: 1, opacity: 1 }}
                      className="quantity-display"
                    >
                      {item.quantity}
                    </motion.span>
                    <motion.button
                      onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      whileHover={{ scale: 1.2 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      +
                    </motion.button>
                  </div>

                  <div className="item-total">
                    <span>${(item.price * item.quantity).toFixed(2)}</span>
                  </div>

                  <motion.button 
                    className="remove-item-btn"
                    onClick={() => handleRemove(item.id)}
                    whileHover={{ scale: 1.2, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <span>‚úï</span>
                  </motion.button>
                </motion.div>
              ))}
            </AnimatePresence>

            {/* Recommended Products */}
            <motion.div 
              className="recommended-section"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
            >
              <h3>üî• You might also like</h3>
              <p>Complete your purchase with these items</p>
            </motion.div>
          </div>

          {/* Order Summary */}
          <motion.div 
            className="cart-summary"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.3 }}
          >
            <div className="summary-header">
              <h2>Order Summary</h2>
            </div>

            <div className="summary-details">
              <div className="summary-row">
                <span>Subtotal ({cartItems.length} items)</span>
                <span>${subtotal.toFixed(2)}</span>
              </div>
              
              <div className="summary-row">
                <span>Shipping</span>
                <span className={shipping === 0 ? 'free-shipping' : ''}>
                  {shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}
                </span>
              </div>

              <div className="summary-row">
                <span>Tax (8%)</span>
                <span>${tax.toFixed(2)}</span>
              </div>

              {discount > 0 && (
                <motion.div 
                  className="summary-row discount-row"
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={{ opacity: 1, scale: 1 }}
                >
                  <span>üéÅ Discount (10%)</span>
                  <span className="discount-amount">-${discount.toFixed(2)}</span>
                </motion.div>
              )}

              <div className="summary-row total-row">
                <span>Total</span>
                <motion.span 
                  className="total-amount"
                  key={finalTotal}
                  initial={{ scale: 1.2 }}
                  animate={{ scale: 1 }}
                >
                  ${finalTotal.toFixed(2)}
                </motion.span>
              </div>
            </div>

            <div className="summary-actions">
              <motion.button
                className="checkout-btn"
                onClick={() => navigate('/checkout')}
                whileHover={{ scale: 1.05, boxShadow: '0 15px 50px rgba(102, 126, 234, 0.4)' }}
                whileTap={{ scale: 0.98 }}
              >
                <span>üîí</span>
                <span>Proceed to Checkout</span>
              </motion.button>

              <Link to="/">
                <motion.button 
                  className="continue-btn"
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  ‚Üê Continue Shopping
                </motion.button>
              </Link>
            </div>

            <div className="trust-badges">
              <div className="badge-item">
                <span>üîí</span>
                <small>Secure Payment</small>
              </div>
              <div className="badge-item">
                <span>üì¶</span>
                <small>Fast Delivery</small>
              </div>
              <div className="badge-item">
                <span>‚Ü©Ô∏è</span>
                <small>Easy Returns</small>
              </div>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Clear Cart Confirmation Modal */}
      <AnimatePresence>
        {showClearConfirm && (
          <>
            <motion.div
              className="modal-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setShowClearConfirm(false)}
            />
            <motion.div
              className="confirm-modal"
              initial={{ opacity: 0, scale: 0.8, y: -50 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.8, y: -50 }}
            >
              <h3>Clear Cart?</h3>
              <p>Are you sure you want to remove all items from your cart?</p>
              <div className="modal-actions">
                <motion.button
                  className="btn-cancel"
                  onClick={() => setShowClearConfirm(false)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Cancel
                </motion.button>
                <motion.button
                  className="btn-confirm"
                  onClick={handleClearCart}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Clear Cart
                </motion.button>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default CartPage;
