import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import ProductCard from '../components/ProductCard';
import LoadingSpinner from '../components/LoadingSpinner';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('featured');
  const [priceRange, setPriceRange] = useState([0, 500]);
  const [searchParams] = useSearchParams();
  const searchQuery = searchParams.get('search');

  const [heroRef, heroInView] = useInView({ threshold: 0.3, triggerOnce: true });
  const [filtersRef, filtersInView] = useInView({ threshold: 0.2, triggerOnce: true });

  const mockProducts = [
    {
      id: 1,
      name: 'Wireless Bluetooth Headphones',
      price: 99.99,
      originalPrice: 129.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
      rating: 4.5,
      reviews: 128,
      description: 'High-quality wireless Bluetooth headphones with noise cancellation.',
      features: ['Active Noise Cancellation', '30-hour battery', 'Comfortable design'],
      inStock: true,
      isNew: true,
      isFeatured: true
    },
    {
      id: 2,
      name: 'Smart Fitness Watch',
      price: 199.99,
      originalPrice: 249.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400',
      rating: 4.3,
      reviews: 89,
      description: 'Advanced fitness tracking with heart rate monitoring.',
      features: ['Heart Rate Monitor', 'GPS Tracking', 'Water Resistant'],
      inStock: true,
      isNew: false,
      isFeatured: true
    },
    {
      id: 3,
      name: 'Premium Cotton T-Shirt',
      price: 24.99,
      category: 'clothing',
      image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=400',
      rating: 4.2,
      reviews: 256,
      description: 'Comfortable cotton t-shirt for everyday wear.',
      features: ['100% Cotton', 'Machine Washable', 'Multiple Colors'],
      inStock: true,
      isNew: true,
      isFeatured: false
    },
    {
      id: 4,
      name: 'Running Shoes',
      price: 129.99,
      category: 'footwear',
      image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=400',
      rating: 4.7,
      reviews: 342,
      description: 'Lightweight running shoes with superior cushioning.',
      features: ['Lightweight', 'Breathable', 'Shock Absorption'],
      inStock: true,
      isNew: false,
      isFeatured: true
    },
    {
      id: 5,
      name: 'Smart Coffee Maker',
      price: 79.99,
      category: 'home',
      image: 'https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400',
      rating: 4.4,
      reviews: 167,
      description: 'Programmable coffee maker for the perfect brew.',
      features: ['Programmable', '12-Cup Capacity', 'Auto Shut-off'],
      inStock: true,
      isNew: false,
      isFeatured: false
    },
    {
      id: 6,
      name: 'Professional Backpack',
      price: 49.99,
      originalPrice: 69.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400',
      rating: 4.6,
      reviews: 203,
      description: 'Durable backpack with laptop compartment.',
      features: ['Laptop Compartment', 'Water Resistant', 'Multiple Pockets'],
      inStock: true,
      isNew: true,
      isFeatured: false
    },
    {
      id: 7,
      name: 'Wireless Earbuds',
      price: 79.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1590658165737-15a047b8b5e3?w=400',
      rating: 4.3,
      reviews: 189,
      description: 'True wireless earbuds with crystal clear sound.',
      features: ['True Wireless', '24hr Battery', 'Touch Controls'],
      inStock: true,
      isNew: true,
      isFeatured: true
    },
    {
      id: 8,
      name: 'Designer Sunglasses',
      price: 89.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?w=400',
      rating: 4.5,
      reviews: 156,
      description: 'Stylish sunglasses with UV protection.',
      features: ['UV Protection', 'Polarized', 'Lightweight Frame'],
      inStock: true,
      isNew: false,
      isFeatured: false
    }
  ];

  const categories = [
    { id: 'all', name: '🌟 All Products', icon: '🌟', count: mockProducts.length },
    { id: 'electronics', name: '🎧 Electronics', icon: '🎧', count: mockProducts.filter(p => p.category === 'electronics').length },
    { id: 'clothing', name: '👕 Clothing', icon: '👕', count: mockProducts.filter(p => p.category === 'clothing').length },
    { id: 'footwear', name: '👟 Footwear', icon: '👟', count: mockProducts.filter(p => p.category === 'footwear').length },
    { id: 'home', name: '🏠 Home', icon: '🏠', count: mockProducts.filter(p => p.category === 'home').length },
    { id: 'accessories', name: '👜 Accessories', icon: '👜', count: mockProducts.filter(p => p.category === 'accessories').length }
  ];

  const sortOptions = [
    { value: 'featured', label: '⭐ Featured' },
    { value: 'price-low', label: '💰 Price: Low to High' },
    { value: 'price-high', label: '💸 Price: High to Low' },
    { value: 'rating', label: '⭐ Highest Rated' },
    { value: 'newest', label: '🆕 Newest' }
  ];

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1500));
      setProducts(mockProducts);
      setFilteredProducts(mockProducts);
      setLoading(false);
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    let filtered = [...products];

    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    if (searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    filtered = filtered.filter(product =>
      product.price >= priceRange[0] && product.price <= priceRange[1]
    );

    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case 'newest':
        filtered.sort((a, b) => (b.isNew ? 1 : 0) - (a.isNew ? 1 : 0));
        break;
      default:
        filtered.sort((a, b) => (b.isFeatured ? 1 : 0) - (a.isFeatured ? 1 : 0));
    }

    setFilteredProducts(filtered);
  }, [selectedCategory, searchQuery, products, sortBy, priceRange]);

  const resetFilters = () => {
    setSelectedCategory('all');
    setSortBy('featured');
    setPriceRange([0, 500]);
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <>
      {/* Hero Section with Parallax Effect */}
      <motion.section 
        className="hero-section"
        ref={heroRef}
        initial={{ opacity: 0 }}
        animate={{ opacity: heroInView ? 1 : 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="container">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: heroInView ? 1 : 0, x: heroInView ? 0 : -50 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <h1 className="hero-title">
              Discover Amazing
              <span className="highlight">Deals Today!</span>
            </h1>
            <p className="hero-subtitle">
              Shop the latest trends and best deals in our curated collection. 
              Quality products at unbeatable prices.
            </p>
            <div className="hero-actions">
              <button className="btn btn-primary">
                Shop Now 🛍️
              </button>
              <button className="btn btn-secondary">
                View Collections ✨
              </button>
            </div>
          </motion.div>

          <motion.div 
            className="hero-image"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: heroInView ? 1 : 0, scale: heroInView ? 1 : 0.8 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <motion.div 
              className="floating-card card-1"
              whileHover={{ scale: 1.1, rotate: 5 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span>🎧</span>
              <div>Audio</div>
            </motion.div>
            <motion.div 
              className="floating-card card-2"
              whileHover={{ scale: 1.1, rotate: -5 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span>⌚</span>
              <div>Wearables</div>
            </motion.div>
            <motion.div 
              className="floating-card card-3"
              whileHover={{ scale: 1.1, rotate: 5 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <span>👟</span>
              <div>Footwear</div>
            </motion.div>
          </motion.div>
        </div>
      </motion.section>

      {/* Filters Section */}
      <motion.section 
        className="filters-section"
        ref={filtersRef}
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: filtersInView ? 1 : 0, y: filtersInView ? 0 : 30 }}
        transition={{ duration: 0.6 }}
      >
        <div className="container">
          <div className="section-header">
            <h2>Our Products</h2>
            <span className="results-count">
              Showing {filteredProducts.length} products
            </span>
          </div>

          <div className="filters-toolbar">
            <div className="category-filters">
              {categories.map((category, index) => (
                <motion.button
                  key={category.id}
                  className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
                  onClick={() => setSelectedCategory(category.id)}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {category.name}
                  <span className="category-count">{category.count}</span>
                </motion.button>
              ))}
            </div>

            <div className="filter-controls">
              <div className="filter-item">
                <label>
                  Price Range: ${priceRange[0]} - ${priceRange[1]}
                </label>
                <div className="price-range">
                  <input
                    type="range"
                    className="range-slider"
                    min="0"
                    max="500"
                    value={priceRange[1]}
                    onChange={(e) => setPriceRange([0, parseInt(e.target.value)])}
                  />
                </div>
              </div>

              <div className="filter-item">
                <label>Sort By</label>
                <select
                  className="sort-select"
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                >
                  {sortOptions.map(option => (
                    <option key={option.value} value={option.value}>
                      {option.label}
                    </option>
                  ))}
                </select>
              </div>

              <motion.button
                className="reset-filters"
                onClick={resetFilters}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                🔄 Reset Filters
              </motion.button>
            </div>
          </div>
        </div>
      </motion.section>

      {/* Products Grid */}
      <section className="products-section">
        <div className="container">
          {filteredProducts.length > 0 ? (
            <div className="products-grid">
              {filteredProducts.map((product) => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          ) : (
            <motion.div 
              className="no-products"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
            >
              <div className="no-products-icon">😕</div>
              <h3>No Products Found</h3>
              <p>Try adjusting your search criteria or filters</p>
              <button className="btn btn-primary" onClick={resetFilters}>
                Reset Filters
              </button>
            </motion.div>
          )}
        </div>
      </section>

      {/* Features Section */}
      <motion.section 
        className="features-section"
        initial={{ opacity: 0 }}
        whileInView={{ opacity: 1 }}
        viewport={{ once: true, amount: 0.2 }}
        transition={{ duration: 0.8 }}
      >
        <div className="container">
          <div className="features-grid">
            <motion.div 
              className="feature-card"
              whileHover={{ y: -10 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <div className="feature-icon">🚚</div>
              <h3>Free Delivery</h3>
              <p>Free delivery on orders over $50</p>
            </motion.div>

            <motion.div 
              className="feature-card"
              whileHover={{ y: -10 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <div className="feature-icon">↩️</div>
              <h3>Easy Returns</h3>
              <p>7-day return policy</p>
            </motion.div>

            <motion.div 
              className="feature-card"
              whileHover={{ y: -10 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <div className="feature-icon">🔒</div>
              <h3>Secure Payment</h3>
              <p>Your payment information is safe</p>
            </motion.div>

            <motion.div 
              className="feature-card"
              whileHover={{ y: -10 }}
              transition={{ type: "spring", stiffness: 300 }}
            >
              <div className="feature-icon">💬</div>
              <h3>24/7 Support</h3>
              <p>Get help whenever you need</p>
            </motion.div>
          </div>
        </div>
      </motion.section>
    </>
  );
};

export default Home;
