import React, { useState, useEffect } from 'react';
import { useSearchParams, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { useCart } from '../context/CartContext';
import { useTheme } from '../context/ThemeContext';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchParams] = useSearchParams();
  const searchQuery = searchParams.get('search');
  
  const { addToCart } = useCart();
  const { isDarkMode } = useTheme();
  const [heroRef, heroInView] = useInView({ threshold: 0.3, triggerOnce: true });

  const mockProducts = [
    {
      id: 1,
      name: 'Wireless Bluetooth Headphones',
      price: 99.99,
      originalPrice: 129.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
      rating: 4.5,
      reviews: 128,
      isNew: true,
      isFeatured: true
    },
    {
      id: 2,
      name: 'Smart Fitness Watch',
      price: 199.99,
      originalPrice: 249.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400',
      rating: 4.3,
      reviews: 89,
      isFeatured: true
    },
    {
      id: 3,
      name: 'Premium Cotton T-Shirt',
      price: 24.99,
      category: 'clothing',
      image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=400',
      rating: 4.2,
      reviews: 256,
      isNew: true
    },
    {
      id: 4,
      name: 'Running Shoes',
      price: 129.99,
      category: 'footwear',
      image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=400',
      rating: 4.7,
      reviews: 342,
      isFeatured: true
    },
    {
      id: 5,
      name: 'Smart Coffee Maker',
      price: 79.99,
      category: 'home',
      image: 'https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400',
      rating: 4.4,
      reviews: 167
    },
    {
      id: 6,
      name: 'Professional Backpack',
      price: 49.99,
      originalPrice: 69.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400',
      rating: 4.6,
      reviews: 203,
      isNew: true
    },
    {
      id: 7,
      name: 'Wireless Earbuds',
      price: 79.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1590658165737-15a047b8b5e3?w=400',
      rating: 4.3,
      reviews: 189,
      isNew: true,
      isFeatured: true
    },
    {
      id: 8,
      name: 'Designer Sunglasses',
      price: 89.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?w=400',
      rating: 4.5,
      reviews: 156
    }
  ];

  const categories = [
    { id: 'all', name: 'All Products', icon: '⭐', count: mockProducts.length },
    { id: 'electronics', name: 'Electronics', icon: '🎧', count: mockProducts.filter(p => p.category === 'electronics').length },
    { id: 'clothing', name: 'Clothing', icon: '👕', count: mockProducts.filter(p => p.category === 'clothing').length },
    { id: 'footwear', name: 'Footwear', icon: '👟', count: mockProducts.filter(p => p.category === 'footwear').length },
    { id: 'home', name: 'Home', icon: '🏠', count: mockProducts.filter(p => p.category === 'home').length },
    { id: 'accessories', name: 'Accessories', icon: '👜', count: mockProducts.filter(p => p.category === 'accessories').length }
  ];

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1000));
      setProducts(mockProducts);
      setFilteredProducts(mockProducts);
      setLoading(false);
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    let filtered = [...products];

    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    if (searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFilteredProducts(filtered);
  }, [selectedCategory, searchQuery, products]);

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <>
      {/* Hero Section */}
      <motion.section className="hero-section" ref={heroRef}>
        <div className="hero-container">
          <motion.div
            className="hero-content"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: heroInView ? 1 : 0, x: heroInView ? 0 : -50 }}
            transition={{ duration: 0.8 }}
          >
            <h1>
              Discover Amazing
              <span className="hero-highlight"> Deals Today!</span>
            </h1>
            <p>
              Shop the latest trends and best deals in our curated collection. 
              Quality products at unbeatable prices.
            </p>
            <div className="hero-buttons">
              <button className="btn btn-primary">
                Shop Now 🛍️
              </button>
              <button className="btn btn-secondary">
                View Collections ✨
              </button>
            </div>
          </motion.div>

          <motion.div
            className="hero-visual"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: heroInView ? 1 : 0, scale: heroInView ? 1 : 0.8 }}
            transition={{ duration: 0.8, delay: 0.3 }}
          >
            <div className="floating-card card-1">
              <span>🎧</span>
              <div>Audio</div>
            </div>
            <div className="floating-card card-2">
              <span>⌚</span>
              <div>Wearables</div>
            </div>
            <div className="floating-card card-3">
              <span>👟</span>
              <div>Footwear</div>
            </div>
          </motion.div>
        </div>
      </motion.section>

      {/* Products Section */}
      <section className="products-section">
        <div className="section-header">
          <h2>Our Products</h2>
          <span className="results-count">Showing {filteredProducts.length} products</span>
        </div>

        {/* Category Filter */}
        <div className="category-filter">
          {categories.map((category) => (
            <button
              key={category.id}
              className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.id)}
            >
              <span>{category.icon}</span>
              <span>{category.name}</span>
              <span className="category-count">{category.count}</span>
            </button>
          ))}
        </div>

        {/* Products Grid */}
        <div className="products-grid">
          {filteredProducts.map((product) => (
            <motion.div
              key={product.id}
              className="product-card"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              whileHover={{ y: -8 }}
            >
              <Link to={`/product/${product.id}`}>
                <div className="product-image">
                  <img src={product.image} alt={product.name} />
                  <div className="product-badges">
                    {product.originalPrice && (
                      <span className="badge badge-hot">
                        {Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100)}% OFF
                      </span>
                    )}
                    {product.isNew && <span className="badge badge-new">NEW</span>}
                  </div>
                </div>
              </Link>

              <div className="product-info">
                <div className="product-category">{product.category}</div>
                <h3 className="product-name">{product.name}</h3>
                <div className="product-rating">
                  <div className="stars">{'⭐'.repeat(Math.floor(product.rating))}</div>
                  <span className="rating-count">({product.reviews})</span>
                </div>
                <div className="product-price">
                  <span className="price">${product.price}</span>
                  {product.originalPrice && (
                    <span className="original-price">${product.originalPrice}</span>
                  )}
                </div>
                <button 
                  className="add-to-cart"
                  onClick={() => addToCart(product)}
                >
                  <span>🛒</span>
                  <span>Add to Cart</span>
                </button>
              </div>
            </motion.div>
          ))}
        </div>
      </section>
    </>
  );
};

export default Home;
