import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useTheme } from '../context/ThemeContext';
import AuthModal from './AuthModal';
import Cart from './Cart';

const Header = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const { getCartItemsCount, getWishlistCount } = useCart();
  const { isDark, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const searchInputRef = useRef(null);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/?search=${searchQuery}`);
      setSearchQuery('');
    }
  };

  const handleQuickSearch = (query) => {
    setSearchQuery(query);
    navigate(`/?search=${query}`);
    setSearchQuery('');
  };

  const quickSearches = ['Headphones', 'Smartwatch', 'Shoes', 'Laptop'];

  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  return (
    <>
      <header className="header">
        <div className="container">
          <div className="header-content">
            <div className="header-left">
              <button 
                className="mobile-menu-btn"
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              >
                <span></span>
                <span></span>
                <span></span>
              </button>
              
              <Link to="/" className="logo">
                <span className="logo-icon">🛍️</span>
                <span className="logo-text">ShopEasy</span>
              </Link>
            </div>

            <form onSubmit={handleSearch} className="search-bar">
              <div className="search-container">
                <input
                  ref={searchInputRef}
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                <button type="submit" className="search-btn">
                  <span className="search-icon">🔍</span>
                </button>
                
                {searchQuery && (
                  <div className="search-suggestions">
                    {quickSearches
                      .filter(item => 
                        item.toLowerCase().includes(searchQuery.toLowerCase())
                      )
                      .map((suggestion, index) => (
                        <div
                          key={index}
                          className="suggestion-item"
                          onClick={() => handleQuickSearch(suggestion)}
                        >
                          {suggestion}
                        </div>
                      ))
                    }
                  </div>
                )}
              </div>
            </form>

            <nav className="nav">
              <button 
                className="theme-toggle"
                onClick={toggleTheme}
                title={isDark ? 'Switch to light mode' : 'Switch to dark mode'}
              >
                {isDark ? '☀️' : '🌙'}
              </button>
              
              <Link to="/wishlist" className="nav-link wishlist-link">
                <span className="nav-icon">❤️</span>
                {getWishlistCount() > 0 && (
                  <span className="nav-badge">{getWishlistCount()}</span>
                )}
              </Link>
              
              <button 
                className="auth-btn"
                onClick={() => setIsAuthModalOpen(true)}
              >
                <span className="auth-icon">👤</span>
                <span className="auth-text">Login</span>
              </button>
              
              <button 
                className="cart-btn"
                onClick={() => setIsCartOpen(true)}
              >
                <span className="cart-icon">🛒</span>
                {getCartItemsCount() > 0 && (
                  <span className="cart-count">{getCartItemsCount()}</span>
                )}
              </button>
            </nav>
          </div>

          {/* Mobile Menu */}
          <div className={`mobile-menu ${isMobileMenuOpen ? 'open' : ''}`}>
            <nav className="mobile-nav">
              <Link to="/" className="mobile-nav-link">Home</Link>
              <Link to="/categories" className="mobile-nav-link">Categories</Link>
              <Link to="/deals" className="mobile-nav-link">Deals</Link>
              <Link to="/wishlist" className="mobile-nav-link">
                Wishlist {getWishlistCount() > 0 && `(${getWishlistCount()})`}
              </Link>
              <button 
                className="mobile-nav-link"
                onClick={() => setIsAuthModalOpen(true)}
              >
                Account
              </button>
            </nav>
          </div>
        </div>
      </header>

      {/* Overlay for mobile menu */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        ></div>
      )}

      <AuthModal 
        isOpen={isAuthModalOpen}
        onClose={() => setIsAuthModalOpen(false)}
      />
      
      <Cart 
        isOpen={isCartOpen}
        onClose={() => setIsCartOpen(false)}
      />
    </>
  );
};

export default Header;
