import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence, useScroll, useTransform } from 'framer-motion';
import { useCart } from '../context/CartContext';
import { useTheme } from '../context/ThemeContext';
import AuthModal from './AuthModal';

const Header = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchFocused, setIsSearchFocused] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [scrollDirection, setScrollDirection] = useState('up');
  const [lastScrollY, setLastScrollY] = useState(0);
  
  const { getCartItemsCount } = useCart();
  const { isDarkMode, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const headerRef = useRef(null);

  const { scrollY } = useScroll();
  const headerOpacity = useTransform(scrollY, [0, 100], [1, 0.98]);

  // Navigation items
  const navItems = [
    { name: 'Home', path: '/', icon: '🏠', exact: true },
    { 
      name: 'Shop', 
      path: '/shop', 
      icon: '🛍️',
      dropdown: [
        { name: 'Electronics', path: '/?category=electronics', icon: '🎧' },
        { name: 'Clothing', path: '/?category=clothing', icon: '👕' },
        { name: 'Footwear', path: '/?category=footwear', icon: '👟' },
        { name: 'Accessories', path: '/?category=accessories', icon: '👜' },
        { name: 'Home & Living', path: '/?category=home', icon: '🏠' }
      ]
    },
    { name: 'Deals', path: '/deals', icon: '🔥', badge: 'Hot' },
    { name: 'New Arrivals', path: '/new', icon: '✨', badge: 'New' },
    { name: 'Contact', path: '/contact', icon: '💬' }
  ];

  // Scroll effect
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      
      if (currentScrollY > lastScrollY && currentScrollY > 100) {
        setScrollDirection('down');
      } else {
        setScrollDirection('up');
      }
      
      setLastScrollY(currentScrollY);
      setIsScrolled(currentScrollY > 50);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  useEffect(() => {
    setIsMobileMenuOpen(false);
    setActiveDropdown(null);
  }, [location]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/?search=${searchQuery}`);
      setSearchQuery('');
      setIsSearchFocused(false);
    }
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    document.body.style.overflow = !isMobileMenuOpen ? 'hidden' : 'unset';
  };

  const isActive = (path, exact = false) => {
    if (exact) return location.pathname === path;
    return location.pathname.startsWith(path);
  };

  const cartItemsCount = getCartItemsCount();

  return (
    <>
      <motion.header
        ref={headerRef}
        className={`header ${isScrolled ? 'scrolled' : ''} ${scrollDirection === 'down' && isScrolled ? 'hidden' : ''}`}
        initial="visible"
        animate={scrollDirection === 'down' && isScrolled && !isMobileMenuOpen ? 'hidden' : 'visible'}
        variants={{
          visible: { y: 0, opacity: 1 },
          hidden: { y: -100, opacity: 0 }
        }}
        style={{ opacity: headerOpacity }}
      >
        <div className="header-container">
          <div className="header-content">
            {/* Logo */}
            <motion.div className="header-logo-section" whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
              <Link to="/" className="logo">
                <motion.span 
                  className="logo-icon"
                  animate={{ rotate: [0, 10, -10, 0] }}
                  transition={{ duration: 2, repeat: Infinity, repeatDelay: 3 }}
                >
                  🛒
                </motion.span>
                <span className="logo-text">ShopHub</span>
                <motion.span 
                  className="logo-badge"
                  animate={{ scale: [1, 1.1, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  Pro
                </motion.span>
              </Link>
            </motion.div>

            {/* Desktop Navigation */}
            <nav className="desktop-nav">
              <ul className="nav-list">
                {navItems.map((item, index) => (
                  <motion.li
                    key={item.name}
                    className="nav-item"
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                    onMouseEnter={() => item.dropdown && setActiveDropdown(item.name)}
                    onMouseLeave={() => item.dropdown && setActiveDropdown(null)}
                  >
                    <Link to={item.path} className={`nav-link ${isActive(item.path, item.exact) ? 'active' : ''}`}>
                      <span className="nav-icon">{item.icon}</span>
                      <span className="nav-text">{item.name}</span>
                      {item.badge && (
                        <motion.span 
                          className={`nav-badge ${item.badge.toLowerCase()}`}
                          animate={{ scale: [1, 1.2, 1] }}
                          transition={{ duration: 1.5, repeat: Infinity }}
                        >
                          {item.badge}
                        </motion.span>
                      )}
                      {item.dropdown && <span className="dropdown-arrow">▼</span>}
                    </Link>

                    <AnimatePresence>
                      {item.dropdown && activeDropdown === item.name && (
                        <motion.div
                          className="dropdown-menu"
                          initial={{ opacity: 0, y: -10, scale: 0.95 }}
                          animate={{ opacity: 1, y: 0, scale: 1 }}
                          exit={{ opacity: 0, y: -10, scale: 0.95 }}
                        >
                          <div className="dropdown-content">
                            {item.dropdown.map((subItem, subIndex) => (
                              <motion.div
                                key={subItem.name}
                                initial={{ opacity: 0, x: -10 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ delay: subIndex * 0.05 }}
                              >
                                <Link to={subItem.path} className="dropdown-item">
                                  <span className="dropdown-icon">{subItem.icon}</span>
                                  <span className="dropdown-text">{subItem.name}</span>
                                </Link>
                              </motion.div>
                            ))}
                          </div>
                        </motion.div>
                      )}
                    </AnimatePresence>
                  </motion.li>
                ))}
              </ul>
            </nav>

            {/* Search Bar */}
            <motion.div 
              className={`search-bar ${isSearchFocused ? 'focused' : ''}`}
              animate={{ width: isSearchFocused ? '400px' : '280px' }}
            >
              <form className="search-container" onSubmit={handleSearch}>
                <motion.span className="search-icon" animate={{ rotate: isSearchFocused ? 90 : 0 }}>
                  🔍
                </motion.span>
                <input
                  type="text"
                  className="search-input"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onFocus={() => setIsSearchFocused(true)}
                  onBlur={() => setIsSearchFocused(false)}
                />
                <AnimatePresence>
                  {searchQuery && (
                    <motion.button
                      type="button"
                      className="search-clear"
                      onClick={() => setSearchQuery('')}
                      initial={{ scale: 0 }}
                      animate={{ scale: 1 }}
                      exit={{ scale: 0 }}
                      whileHover={{ scale: 1.2, rotate: 90 }}
                    >
                      ✕
                    </motion.button>
                  )}
                </AnimatePresence>
              </form>
            </motion.div>

            {/* Header Actions */}
            <div className="header-actions">
              {/* Dark Mode Toggle */}
              <motion.button
                className="action-btn theme-toggle"
                onClick={toggleTheme}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                title={isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
              >
                <motion.span
                  className="action-icon"
                  initial={false}
                  animate={{ rotate: isDarkMode ? 180 : 0 }}
                  transition={{ duration: 0.5 }}
                >
                  {isDarkMode ? '☀️' : '🌙'}
                </motion.span>
              </motion.button>

              {/* Wishlist */}
              <motion.button
                className="action-btn wishlist-btn"
                whileHover={{ scale: 1.1, rotate: [0, -10, 10, 0] }}
                whileTap={{ scale: 0.95 }}
                title="Wishlist"
              >
                <span className="action-icon">❤️</span>
                <motion.span className="action-badge" animate={{ scale: [1, 1.2, 1] }} transition={{ duration: 2, repeat: Infinity }}>
                  3
                </motion.span>
              </motion.button>

              {/* Notifications */}
              <motion.button
                className="action-btn notifications-btn"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                title="Notifications"
              >
                <span className="action-icon">🔔</span>
                <motion.span className="notification-dot" animate={{ scale: [1, 1.3, 1] }} transition={{ duration: 1.5, repeat: Infinity }} />
              </motion.button>

              {/* Auth Button */}
              <motion.button
                className="auth-btn"
                onClick={() => setIsAuthModalOpen(true)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <span className="auth-icon">👤</span>
                <span className="auth-text">Sign In</span>
              </motion.button>

              {/* Cart */}
              <motion.div whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                <Link to="/cart" className="cart-btn">
                  <motion.span className="cart-icon" animate={{ rotate: cartItemsCount > 0 ? [0, -10, 10, 0] : 0 }}>
                    🛒
                  </motion.span>
                  <AnimatePresence>
                    {cartItemsCount > 0 && (
                      <motion.span
                        className="cart-count"
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        exit={{ scale: 0 }}
                        key={cartItemsCount}
                      >
                        {cartItemsCount}
                      </motion.span>
                    )}
                  </AnimatePresence>
                </Link>
              </motion.div>

              {/* Mobile Menu Toggle */}
              <motion.button
                className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
                onClick={toggleMobileMenu}
                whileTap={{ scale: 0.95 }}
              >
                <span></span>
                <span></span>
                <span></span>
              </motion.button>
            </div>
          </div>

          {/* Quick Access Bar */}
          <AnimatePresence>
            {isScrolled && (
              <motion.div
                className="quick-access-bar"
                initial={{ height: 0, opacity: 0 }}
                animate={{ height: 'auto', opacity: 1 }}
                exit={{ height: 0, opacity: 0 }}
              >
                <div className="quick-access-content">
                  <motion.div className="promo-banner" animate={{ x: [0, -10, 0] }} transition={{ duration: 2, repeat: Infinity }}>
                    <span className="promo-icon">🎉</span>
                    <span className="promo-text">Free Shipping on Orders Over $50!</span>
                  </motion.div>
                  <div className="quick-links">
                    <a href="#deals" className="quick-link">🔥 Hot Deals</a>
                    <a href="#new" className="quick-link">✨ New Arrivals</a>
                    <a href="#sale" className="quick-link">💰 Sale</a>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.header>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <>
            <motion.div
              className="mobile-menu-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={toggleMobileMenu}
            />
            <motion.div
              className="mobile-menu"
              initial={{ x: '100%' }}
              animate={{ x: 0 }}
              exit={{ x: '100%' }}
            >
              <div className="mobile-menu-header">
                <div className="mobile-logo">
                  <span className="logo-icon">🛒</span>
                  <span className="logo-text">ShopHub</span>
                </div>
                <motion.button
                  className="mobile-close-btn"
                  onClick={toggleMobileMenu}
                  whileHover={{ rotate: 90, scale: 1.1 }}
                >
                  ✕
                </motion.button>
              </div>

              <div className="mobile-menu-content">
                <div className="mobile-search">
                  <form onSubmit={handleSearch}>
                    <input type="text" placeholder="Search products..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
                    <button type="submit">🔍</button>
                  </form>
                </div>

                <nav className="mobile-nav">
                  {navItems.map((item, index) => (
                    <motion.div
                      key={item.name}
                      className="mobile-nav-item"
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                    >
                      {item.dropdown ? (
                        <>
                          <button
                            className={`mobile-nav-link ${activeDropdown === item.name ? 'active' : ''}`}
                            onClick={() => setActiveDropdown(activeDropdown === item.name ? null : item.name)}
                          >
                            <span className="nav-icon">{item.icon}</span>
                            <span className="nav-text">{item.name}</span>
                            <motion.span className="dropdown-arrow" animate={{ rotate: activeDropdown === item.name ? 180 : 0 }}>
                              ▼
                            </motion.span>
                          </button>
                          <AnimatePresence>
                            {activeDropdown === item.name && (
                              <motion.div
                                className="mobile-dropdown"
                                initial={{ height: 0, opacity: 0 }}
                                animate={{ height: 'auto', opacity: 1 }}
                                exit={{ height: 0, opacity: 0 }}
                              >
                                {item.dropdown.map((subItem) => (
                                  <Link key={subItem.name} to={subItem.path} className="mobile-dropdown-item" onClick={toggleMobileMenu}>
                                    <span className="dropdown-icon">{subItem.icon}</span>
                                    <span className="dropdown-text">{subItem.name}</span>
                                  </Link>
                                ))}
                              </motion.div>
                            )}
                          </AnimatePresence>
                        </>
                      ) : (
                        <Link to={item.path} className={`mobile-nav-link ${isActive(item.path, item.exact) ? 'active' : ''}`} onClick={toggleMobileMenu}>
                          <span className="nav-icon">{item.icon}</span>
                          <span className="nav-text">{item.name}</span>
                          {item.badge && <span className={`nav-badge ${item.badge.toLowerCase()}`}>{item.badge}</span>}
                        </Link>
                      )}
                    </motion.div>
                  ))}
                </nav>

                <div className="mobile-user-section">
                  <button className="mobile-theme-toggle" onClick={toggleTheme}>
                    <span>{isDarkMode ? '☀️' : '🌙'}</span>
                    <span>Toggle {isDarkMode ? 'Light' : 'Dark'} Mode</span>
                  </button>
                  <button className="mobile-auth-btn" onClick={() => { setIsAuthModalOpen(true); toggleMobileMenu(); }}>
                    <span>👤</span>
                    <span>Sign In / Register</span>
                  </button>
                </div>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>

      {isAuthModalOpen && <AuthModal onClose={() => setIsAuthModalOpen(false)} />}
    </>
  );
};

export default Header;
