import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useCart } from '../context/CartContext';
import { useTheme } from '../context/ThemeContext';
import AuthModal from './AuthModal';

const Header = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isScrolled, setIsScrolled] = useState(false);
  
  const { getCartItemsCount } = useCart();
  const { isDarkMode, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/?search=${searchQuery}`);
    }
  };

  const navItems = [
    { name: 'Home', path: '/', icon: '🏠', badge: null },
    { name: 'Shop', path: '/shop', icon: '🛍️', badge: null },
    { name: 'Deals', path: '/deals', icon: '🔥', badge: 'Hot' },
    { name: 'New Arrivals', path: '/new', icon: '✨', badge: 'New' },
    { name: 'Contact', path: '/contact', icon: '💬', badge: null }
  ];

  const isActive = (path) => location.pathname === path;
  const cartCount = getCartItemsCount();

  return (
    <>
      <motion.header 
        className={`header ${isScrolled ? 'scrolled' : ''}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className="header-wrapper">
          <div className="header-content">
            {/* Logo */}
            <Link to="/" className="logo">
              <motion.span 
                className="logo-icon"
                whileHover={{ rotate: [0, -10, 10, 0] }}
                transition={{ duration: 0.5 }}
              >
                🛒
              </motion.span>
              <span className="logo-text">ShopHub</span>
              <span className="logo-badge">Pro</span>
            </Link>

            {/* Desktop Navigation */}
            <nav className="main-nav">
              {navItems.map((item) => (
                <Link
                  key={item.name}
                  to={item.path}
                  className={`nav-link ${isActive(item.path) ? 'active' : ''}`}
                >
                  <span>{item.icon}</span>
                  <span>{item.name}</span>
                  {item.badge && (
                    <span className={`nav-badge ${item.badge.toLowerCase()}`}>
                      {item.badge}
                    </span>
                  )}
                </Link>
              ))}
            </nav>

            {/* Search Bar */}
            <div className="header-search">
              <form onSubmit={handleSearch} className="search-wrapper">
                <span className="search-icon">🔍</span>
                <input
                  type="text"
                  className="search-input"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </form>
            </div>

            {/* Header Actions */}
            <div className="header-actions">
              {/* Theme Toggle */}
              <motion.button
                className="icon-btn theme-toggle"
                onClick={toggleTheme}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                title={isDarkMode ? 'Light Mode' : 'Dark Mode'}
              >
                {isDarkMode ? '☀️' : '🌙'}
              </motion.button>

              {/* Wishlist */}
              <motion.button
                className="icon-btn"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={() => navigate('/wishlist')}
              >
                ❤️
                <span className="icon-badge">3</span>
              </motion.button>

              {/* Notifications */}
              <motion.button
                className="icon-btn"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
              >
                🔔
                <span className="icon-badge">5</span>
              </motion.button>

              {/* Sign In Button */}
              <motion.button
                className="sign-in-btn"
                onClick={() => setIsAuthModalOpen(true)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <span>👤</span>
                <span>Sign In</span>
              </motion.button>

              {/* Cart Button */}
              <Link to="/cart">
                <motion.button
                  className="cart-btn"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <span>🛒</span>
                  <span>Cart</span>
                  {cartCount > 0 && (
                    <span className="icon-badge">{cartCount}</span>
                  )}
                </motion.button>
              </Link>

              {/* Mobile Menu Button */}
              <button
                className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              >
                <span></span>
                <span></span>
                <span></span>
              </button>
            </div>
          </div>
        </div>
      </motion.header>

      {/* Mobile Menu Overlay */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <>
            <motion.div
              className="mobile-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setIsMobileMenuOpen(false)}
            />
            <motion.div
              className="mobile-menu"
              initial={{ x: '100%' }}
              animate={{ x: 0 }}
              exit={{ x: '100%' }}
              transition={{ type: 'spring', damping: 25 }}
            >
              <div className="mobile-menu-header">
                <h3>Menu</h3>
                <button onClick={() => setIsMobileMenuOpen(false)}>✕</button>
              </div>
              <nav className="mobile-nav">
                {navItems.map((item) => (
                  <Link
                    key={item.name}
                    to={item.path}
                    className={`mobile-nav-link ${isActive(item.path) ? 'active' : ''}`}
                    onClick={() => setIsMobileMenuOpen(false)}
                  >
                    <span>{item.icon}</span>
                    <span>{item.name}</span>
                    {item.badge && <span className={`nav-badge ${item.badge.toLowerCase()}`}>{item.badge}</span>}
                  </Link>
                ))}
              </nav>
            </motion.div>
          </>
        )}
      </AnimatePresence>

      {/* Auth Modal */}
      {isAuthModalOpen && <AuthModal onClose={() => setIsAuthModalOpen(false)} />}
    </>
  );
};

export default Header;
