import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const ProductCard = ({ product }) => {
  const [imageLoaded, setImageLoaded] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const { addToCart } = useCart();

  const handleAddToCart = (e) => {
    e.preventDefault();
    e.stopPropagation();
    addToCart(product);
    
    // Animation feedback
    const button = e.target;
    button.classList.add('added');
    setTimeout(() => button.classList.remove('added'), 600);
  };

  const getDiscountBadge = () => {
    if (product.originalPrice && product.originalPrice > product.price) {
      const discount = Math.round(((product.originalPrice - product.price) / product.originalPrice) * 100);
      return <span className="discount-badge">-{discount}%</span>;
    }
    return null;
  };

  const getRatingStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push('⭐');
    }
    if (hasHalfStar) {
      stars.push('⭐');
    }
    for (let i = stars.length; i < 5; i++) {
      stars.push('☆');
    }
    return stars;
  };

  return (
    <div 
      className="product-card"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <Link to={`/product/${product.id}`} className="product-link">
        <div className="product-image">
          {!imageLoaded && !imageError && (
            <div className="image-skeleton"></div>
          )}
          <img
            src={product.image}
            alt={product.name}
            onLoad={() => setImageLoaded(true)}
            onError={() => setImageError(true)}
            style={{ display: imageLoaded && !imageError ? 'block' : 'none' }}
          />
          {imageError && (
            <div className="image-error">
              <span className="error-icon">📷</span>
              <span>Image not available</span>
            </div>
          )}
          
          {/* Product badges */}
          <div className="product-badges">
            {getDiscountBadge()}
            {product.isNew && <span className="new-badge">New</span>}
            {product.isFeatured && <span className="featured-badge">🔥</span>}
          </div>

          {/* Quick actions on hover */}
          <div className={`quick-actions ${isHovered ? 'visible' : ''}`}>
            <button className="quick-view-btn" title="Quick View">
              👁️
            </button>
            <button className="wishlist-btn" title="Add to Wishlist">
              ❤️
            </button>
          </div>
        </div>
        
        <div className="product-info">
          <h3 className="product-name">{product.name}</h3>
          <p className="product-category">{product.category}</p>
          
          <div className="product-rating">
            <div className="stars">
              {getRatingStars(product.rating).map((star, index) => (
                <span key={index} className="star">{star}</span>
              ))}
            </div>
            <span className="rating-count">({product.reviews})</span>
          </div>

          <div className="product-pricing">
            <div className="price">${product.price}</div>
            {product.originalPrice && (
              <div className="original-price">${product.originalPrice}</div>
            )}
          </div>
        </div>
      </Link>
      
      <button 
        onClick={handleAddToCart}
        className="add-to-cart-btn"
      >
        <span className="btn-text">Add to Cart</span>
        <span className="btn-icon">+</span>
        <span className="btn-success">✓ Added!</span>
      </button>
    </div>
  );
};

export default ProductCard;
