import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { motion, AnimatePresence, useScroll, useTransform } from 'framer-motion';
import { useCart } from '../context/CartContext';
import AuthModal from './AuthModal';

const Header = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState(false);
  const [isSearchFocused, setIsSearchFocused] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [scrollDirection, setScrollDirection] = useState('up');
  const [lastScrollY, setLastScrollY] = useState(0);
  
  const { getCartItemsCount, cart } = useCart();
  const navigate = useNavigate();
  const location = useLocation();
  const headerRef = useRef(null);
  const searchInputRef = useRef(null);

  const { scrollY } = useScroll();
  const headerOpacity = useTransform(scrollY, [0, 100], [1, 0.98]);
  const headerScale = useTransform(scrollY, [0, 100], [1, 0.97]);

  // Navigation items with icons and dropdown support
  const navItems = [
    { 
      name: 'Home', 
      path: '/', 
      icon: 'ðŸ ',
      exact: true
    },
    { 
      name: 'Shop', 
      path: '/shop', 
      icon: 'ðŸ›ï¸',
      dropdown: [
        { name: 'Electronics', path: '/shop?category=electronics', icon: 'ðŸŽ§' },
        { name: 'Clothing', path: '/shop?category=clothing', icon: 'ðŸ‘•' },
        { name: 'Footwear', path: '/shop?category=footwear', icon: 'ðŸ‘Ÿ' },
        { name: 'Accessories', path: '/shop?category=accessories', icon: 'ðŸ‘œ' },
        { name: 'Home & Living', path: '/shop?category=home', icon: 'ðŸ ' }
      ]
    },
    { 
      name: 'Deals', 
      path: '/deals', 
      icon: 'ðŸ”¥',
      badge: 'Hot'
    },
    { 
      name: 'New Arrivals', 
      path: '/new', 
      icon: 'âœ¨',
      badge: 'New'
    },
    { 
      name: 'Contact', 
      path: '/contact', 
      icon: 'ðŸ’¬'
    }
  ];

  // Enhanced scroll effect with direction detection
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;
      
      // Detect scroll direction
      if (currentScrollY > lastScrollY && currentScrollY > 100) {
        setScrollDirection('down');
      } else {
        setScrollDirection('up');
      }
      
      setLastScrollY(currentScrollY);
      setIsScrolled(currentScrollY > 50);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, [lastScrollY]);

  // Close mobile menu on route change
  useEffect(() => {
    setIsMobileMenuOpen(false);
    setActiveDropdown(null);
  }, [location]);

  // Handle search
  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/?search=${searchQuery}`);
      setSearchQuery('');
      setIsSearchFocused(false);
    }
  };

  // Toggle mobile menu
  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
    if (!isMobileMenuOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
  };

  // Handle dropdown
  const handleDropdownToggle = (itemName) => {
    setActiveDropdown(activeDropdown === itemName ? null : itemName);
  };

  // Check if current path matches nav item
  const isActive = (path, exact = false) => {
    if (exact) {
      return location.pathname === path;
    }
    return location.pathname.startsWith(path);
  };

  // Animation variants
  const headerVariants = {
    visible: { 
      y: 0, 
      opacity: 1,
      transition: { 
        type: 'spring',
        stiffness: 300,
        damping: 30
      }
    },
    hidden: { 
      y: -100, 
      opacity: 0,
      transition: { 
        type: 'spring',
        stiffness: 300,
        damping: 30
      }
    }
  };

  const mobileMenuVariants = {
    closed: {
      x: '100%',
      transition: {
        type: 'spring',
        stiffness: 400,
        damping: 40
      }
    },
    open: {
      x: 0,
      transition: {
        type: 'spring',
        stiffness: 400,
        damping: 40
      }
    }
  };

  const dropdownVariants = {
    hidden: {
      opacity: 0,
      y: -10,
      scale: 0.95,
      transition: {
        duration: 0.2
      }
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        type: 'spring',
        stiffness: 500,
        damping: 30
      }
    }
  };

  const cartItemsCount = getCartItemsCount();

  return (
    <>
      <motion.header
        ref={headerRef}
        className={`header ${isScrolled ? 'scrolled' : ''} ${scrollDirection === 'down' && isScrolled ? 'hidden' : ''}`}
        variants={headerVariants}
        initial="visible"
        animate={scrollDirection === 'down' && isScrolled && !isMobileMenuOpen ? 'hidden' : 'visible'}
        style={{ opacity: headerOpacity }}
      >
        <div className="header-container">
          <div className="header-content">
            {/* Logo Section */}
            <motion.div 
              className="header-logo-section"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Link to="/" className="logo">
                <motion.span 
                  className="logo-icon"
                  animate={{ 
                    rotate: [0, 10, -10, 0],
                  }}
                  transition={{
                    duration: 2,
                    repeat: Infinity,
                    repeatDelay: 3
                  }}
                >
                  ðŸ›’
                </motion.span>
                <span className="logo-text">ShopHub</span>
                <motion.span 
                  className="logo-badge"
                  animate={{ scale: [1, 1.1, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  Pro
                </motion.span>
              </Link>
            </motion.div>

            {/* Desktop Navigation */}
            <nav className="desktop-nav">
              <ul className="nav-list">
                {navItems.map((item, index) => (
                  <motion.li
                    key={item.name}
                    className="nav-item"
                    initial={{ opacity: 0, y: -20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.1 }}
                    onMouseEnter={() => item.dropdown && setActiveDropdown(item.name)}
                    onMouseLeave={() => item.dropdown && setActiveDropdown(null)}
                  >
                    <Link
                      to={item.path}
                      className={`nav-link ${isActive(item.path, item.exact) ? 'active' : ''}`}
                    >
                      <span className="nav-icon">{item.icon}</span>
                      <span className="nav-text">{item.name}</span>
                      {item.badge && (
                        <motion.span 
                          className={`nav-badge ${item.badge.toLowerCase()}`}
                          animate={{ scale: [1, 1.2, 1] }}
                          transition={{ duration: 1.5, repeat: Infinity }}
                        >
                          {item.badge}
                        </motion.span>
                      )}
                      {item.dropdown && (
                        <span className="dropdown-arrow">â–¼</span>
                      )}
                    </Link>

                    {/* Dropdown Menu */}
                    <AnimatePresence>
                      {item.dropdown && activeDropdown === item.name && (
                        <motion.div
                          className="dropdown-menu"
                          variants={dropdownVariants}
                          initial="hidden"
                          animate="visible"
                          exit="hidden"
                        >
                          <div className="dropdown-content">
                            {item.dropdown.map((subItem, subIndex) => (
                              <motion.div
                                key={subItem.name}
                                initial={{ opacity: 0, x: -10 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ delay: subIndex * 0.05 }}
                              >
                                <Link
                                  to={subItem.path}
                                  className="dropdown-item"
                                >
                                  <span className="dropdown-icon">{subItem.icon}</span>
                                  <span className="dropdown-text">{subItem.name}</span>
                                </Link>
                              </motion.div>
                            ))}
                          </div>
                        </motion.div>
                      )}
                    </AnimatePresence>
                  </motion.li>
                ))}
              </ul>
            </nav>

            {/* Search Bar */}
            <motion.div 
              className={`search-bar ${isSearchFocused ? 'focused' : ''}`}
              animate={{ 
                width: isSearchFocused ? '400px' : '280px',
              }}
              transition={{ type: 'spring', stiffness: 300, damping: 30 }}
            >
              <form className="search-container" onSubmit={handleSearch}>
                <motion.span 
                  className="search-icon"
                  animate={{ rotate: isSearchFocused ? 90 : 0 }}
                  transition={{ duration: 0.3 }}
                >
                  ðŸ”
                </motion.span>
                <input
                  ref={searchInputRef}
                  type="text"
                  className="search-input"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onFocus={() => setIsSearchFocused(true)}
                  onBlur={() => setIsSearchFocused(false)}
                />
                <AnimatePresence>
                  {searchQuery && (
                    <motion.button
                      type="button"
                      className="search-clear"
                      onClick={() => setSearchQuery('')}
                      initial={{ scale: 0, opacity: 0 }}
                      animate={{ scale: 1, opacity: 1 }}
                      exit={{ scale: 0, opacity: 0 }}
                      whileHover={{ scale: 1.2, rotate: 90 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      âœ•
                    </motion.button>
                  )}
                </AnimatePresence>
              </form>
            </motion.div>

            {/* Header Actions */}
            <div className="header-actions">
              {/* Wishlist Button */}
              <motion.button
                className="action-btn wishlist-btn"
                whileHover={{ scale: 1.1, rotate: [0, -10, 10, 0] }}
                whileTap={{ scale: 0.95 }}
                title="Wishlist"
              >
                <span className="action-icon">â¤ï¸</span>
                <motion.span 
                  className="action-badge"
                  animate={{ scale: [1, 1.2, 1] }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  3
                </motion.span>
              </motion.button>

              {/* Notifications Button */}
              <motion.button
                className="action-btn notifications-btn"
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                title="Notifications"
              >
                <span className="action-icon">ðŸ””</span>
                <motion.span 
                  className="notification-dot"
                  animate={{ scale: [1, 1.3, 1] }}
                  transition={{ duration: 1.5, repeat: Infinity }}
                />
              </motion.button>

              {/* Auth Button */}
              <motion.button
                className="auth-btn"
                onClick={() => setIsAuthModalOpen(true)}
                whileHover={{ scale: 1.05, boxShadow: '0 8px 20px rgba(102, 126, 234, 0.3)' }}
                whileTap={{ scale: 0.95 }}
              >
                <span className="auth-icon">ðŸ‘¤</span>
                <span className="auth-text">Sign In</span>
              </motion.button>

              {/* Cart Button */}
              <motion.div
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Link to="/cart" className="cart-btn">
                  <motion.span 
                    className="cart-icon"
                    animate={{ 
                      rotate: cartItemsCount > 0 ? [0, -10, 10, 0] : 0 
                    }}
                    transition={{ duration: 0.5 }}
                  >
                    ðŸ›’
                  </motion.span>
                  <AnimatePresence>
                    {cartItemsCount > 0 && (
                      <motion.span
                        className="cart-count"
                        initial={{ scale: 0 }}
                        animate={{ scale: 1 }}
                        exit={{ scale: 0 }}
                        key={cartItemsCount}
                      >
                        {cartItemsCount}
                      </motion.span>
                    )}
                  </AnimatePresence>
                </Link>
              </motion.div>

              {/* Mobile Menu Toggle */}
              <motion.button
                className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
                onClick={toggleMobileMenu}
                whileTap={{ scale: 0.95 }}
              >
                <span></span>
                <span></span>
                <span></span>
              </motion.button>
            </div>
          </div>

          {/* Quick Access Bar (below main header on scroll) */}
          <AnimatePresence>
            {isScrolled && (
              <motion.div
                className="quick-access-bar"
                initial={{ height: 0, opacity: 0 }}
                animate={{ height: 'auto', opacity: 1 }}
                exit={{ height: 0, opacity: 0 }}
                transition={{ duration: 0.3 }}
              >
                <div className="quick-access-content">
                  <motion.div 
                    className="promo-banner"
                    animate={{ x: [0, -10, 0] }}
                    transition={{ duration: 2, repeat: Infinity }}
                  >
                    <span className="promo-icon">ðŸŽ‰</span>
                    <span className="promo-text">Free Shipping on Orders Over $50!</span>
                  </motion.div>
                  <div className="quick-links">
                    <a href="#deals" className="quick-link">ðŸ”¥ Hot Deals</a>
                    <a href="#new" className="quick-link">âœ¨ New Arrivals</a>
                    <a href="#sale" className="quick-link">ðŸ’° Sale</a>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.header>

      {/* Mobile Menu Overlay */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <>
            <motion.div
              className="mobile-menu-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={toggleMobileMenu}
            />
            <motion.div
              className="mobile-menu"
              variants={mobileMenuVariants}
              initial="closed"
              animate="open"
              exit="closed"
            >
              <div className="mobile-menu-header">
                <div className="mobile-logo">
                  <span className="logo-icon">ðŸ›’</span>
                  <span className="logo-text">ShopHub</span>
                </div>
                <motion.button
                  className="mobile-close-btn"
                  onClick={toggleMobileMenu}
                  whileHover={{ rotate: 90, scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                >
                  âœ•
                </motion.button>
              </div>

              <div className="mobile-menu-content">
                {/* Mobile Search */}
                <div className="mobile-search">
                  <form onSubmit={handleSearch}>
                    <input
                      type="text"
                      placeholder="Search products..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                    />
                    <button type="submit">ðŸ”</button>
                  </form>
                </div>

                {/* Mobile Navigation */}
                <nav className="mobile-nav">
                  {navItems.map((item, index) => (
                    <motion.div
                      key={item.name}
                      className="mobile-nav-item"
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: index * 0.1 }}
                    >
                      {item.dropdown ? (
                        <>
                          <button
                            className={`mobile-nav-link ${activeDropdown === item.name ? 'active' : ''}`}
                            onClick={() => handleDropdownToggle(item.name)}
                          >
                            <span className="nav-icon">{item.icon}</span>
                            <span className="nav-text">{item.name}</span>
                            <motion.span 
                              className="dropdown-arrow"
                              animate={{ rotate: activeDropdown === item.name ? 180 : 0 }}
                            >
                              â–¼
                            </motion.span>
                          </button>
                          <AnimatePresence>
                            {activeDropdown === item.name && (
                              <motion.div
                                className="mobile-dropdown"
                                initial={{ height: 0, opacity: 0 }}
                                animate={{ height: 'auto', opacity: 1 }}
                                exit={{ height: 0, opacity: 0 }}
                              >
                                {item.dropdown.map((subItem) => (
                                  <Link
                                    key={subItem.name}
                                    to={subItem.path}
                                    className="mobile-dropdown-item"
                                    onClick={toggleMobileMenu}
                                  >
                                    <span className="dropdown-icon">{subItem.icon}</span>
                                    <span className="dropdown-text">{subItem.name}</span>
                                  </Link>
                                ))}
                              </motion.div>
                            )}
                          </AnimatePresence>
                        </>
                      ) : (
                        <Link
                          to={item.path}
                          className={`mobile-nav-link ${isActive(item.path, item.exact) ? 'active' : ''}`}
                          onClick={toggleMobileMenu}
                        >
                          <span className="nav-icon">{item.icon}</span>
                          <span className="nav-text">{item.name}</span>
                          {item.badge && (
                            <span className={`nav-badge ${item.badge.toLowerCase()}`}>
                              {item.badge}
                            </span>
                          )}
                        </Link>
                      )}
                    </motion.div>
                  ))}
                </nav>

                {/* Mobile User Section */}
                <div className="mobile-user-section">
                  <button 
                    className="mobile-auth-btn"
                    onClick={() => {
                      setIsAuthModalOpen(true);
                      toggleMobileMenu();
                    }}
                  >
                    <span>ðŸ‘¤</span>
                    <span>Sign In / Register</span>
                  </button>
                </div>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>

      {/* Auth Modal */}
      {isAuthModalOpen && (
        <AuthModal onClose={() => setIsAuthModalOpen(false)} />
      )}
    </>
  );
};

export default Header;
