import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const UserDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('orders');
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch user orders
  useEffect(() => {
    const fetchOrders = async () => {
      if (!user) return;

      try {
        setLoading(true);
        const token = localStorage.getItem('authToken');
        
        const response = await axios.get(
          'http://localhost:5000/api/orders',
          {
            headers: { 'Authorization': `Bearer ${token}` }
          }
        );

        console.log('üì¶ Fetched orders:', response.data);
        setOrders(response.data);
        setLoading(false);
      } catch (error) {
        console.error('‚ùå Error fetching orders:', error);
        setLoading(false);
      }
    };

    fetchOrders();
  }, [user]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: '#ffa500',
      processing: '#3498db',
      shipped: '#9b59b6',
      delivered: '#27ae60',
      cancelled: '#e74c3c'
    };
    return colors[status] || '#95a5a6';
  };

  const tabs = [
    { id: 'orders', label: 'My Orders', icon: 'üì¶' },
    { id: 'profile', label: 'Profile', icon: 'üë§' },
    { id: 'addresses', label: 'Addresses', icon: 'üìç' },
    { id: 'settings', label: 'Settings', icon: '‚öôÔ∏è' }
  ];

  return (
    <div className="user-dashboard">
      {/* Sidebar */}
      <aside className="dashboard-sidebar">
        <div className="sidebar-header">
          <div className="user-avatar">
            <span>{user?.name?.charAt(0).toUpperCase()}</span>
          </div>
          <div className="user-info">
            <h3>{user?.name}</h3>
            <p>{user?.email}</p>
          </div>
        </div>

        <nav className="dashboard-nav">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              className={`nav-item ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
            >
              <span className="nav-icon">{tab.icon}</span>
              <span>{tab.label}</span>
            </button>
          ))}
        </nav>

        <button className="logout-btn" onClick={handleLogout}>
          <span>üö™</span>
          <span>Logout</span>
        </button>
      </aside>

      {/* Main Content */}
      <main className="dashboard-main">
        {activeTab === 'orders' && (
          <div className="dashboard-content">
            <div className="content-header">
              <h1>My Orders</h1>
              <p>Track and manage your orders</p>
            </div>

            {loading ? (
              <div className="loading-state">
                <div className="spinner"></div>
                <p>Loading orders...</p>
              </div>
            ) : orders.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">üì¶</div>
                <h3>No orders yet</h3>
                <p>Start shopping and your orders will appear here</p>
                <button onClick={() => navigate('/')} className="shop-now-btn">
                  Start Shopping
                </button>
              </div>
            ) : (
              <div className="orders-grid">
                {orders.map((order) => (
                  <motion.div
                    key={order._id}
                    className="order-card"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    whileHover={{ y: -5 }}
                  >
                    <div className="order-header">
                      <div className="order-info">
                        <h3>Order #{order.orderNumber}</h3>
                        <p className="order-date">
                          {new Date(order.createdAt).toLocaleDateString('en-US', {
                            year: 'numeric',
                            month: 'long',
                            day: 'numeric'
                          })}
                        </p>
                      </div>
                      <div
                        className="order-status"
                        style={{ backgroundColor: getStatusColor(order.status) }}
                      >
                        {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                      </div>
                    </div>

                    <div className="order-items">
                      {order.items?.slice(0, 2).map((item, idx) => (
                        <div key={idx} className="order-item">
                          <img src={item.image} alt={item.name} />
                          <div className="item-details">
                            <h4>{item.name}</h4>
                            <p>Qty: {item.quantity}</p>
                          </div>
                        </div>
                      ))}
                      {order.items?.length > 2 && (
                        <p className="more-items">+{order.items.length - 2} more items</p>
                      )}
                    </div>

                    <div className="order-footer">
                      <div className="order-total">
                        <span>Total:</span>
                        <strong>${order.total?.toFixed(2)}</strong>
                      </div>
                      <button
                        className="track-btn"
                        onClick={() => navigate(`/order-tracking/${order.orderNumber}`)}
                      >
                        Track Order
                      </button>
                    </div>
                  </motion.div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Profile Tab */}
        {activeTab === 'profile' && (
          <div className="dashboard-content">
            <div className="content-header">
              <h1>Profile Settings</h1>
              <p>Manage your personal information</p>
            </div>
            <div className="profile-form">
              <div className="form-group">
                <label>Full Name</label>
                <input type="text" defaultValue={user?.name} />
              </div>
              <div className="form-group">
                <label>Email</label>
                <input type="email" defaultValue={user?.email} disabled />
              </div>
              <button className="save-btn">Save Changes</button>
            </div>
          </div>
        )}

        {/* Other tabs content */}
        {activeTab === 'addresses' && (
          <div className="dashboard-content">
            <div className="content-header">
              <h1>Delivery Addresses</h1>
              <p>Manage your delivery addresses</p>
            </div>
            <div className="empty-state">
              <p>No addresses saved yet</p>
            </div>
          </div>
        )}

        {activeTab === 'settings' && (
          <div className="dashboard-content">
            <div className="content-header">
              <h1>Account Settings</h1>
              <p>Customize your account preferences</p>
            </div>
            <div className="settings-list">
              <div className="setting-item">
                <span>Email Notifications</span>
                <input type="checkbox" defaultChecked />
              </div>
              <div className="setting-item">
                <span>SMS Notifications</span>
                <input type="checkbox" />
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default UserDashboard;
