import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { useCart } from '../context/CartContext';

const Wishlist = () => {
  // Mock wishlist data (you can create a WishlistContext later if needed)
  const [wishlist, setWishlist] = useState([
    {
      id: 1,
      name: 'Wireless Bluetooth Headphones',
      price: 99.99,
      originalPrice: 129.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
      rating: 4.5,
      reviews: 128,
      inStock: true
    },
    {
      id: 2,
      name: 'Smart Fitness Watch',
      price: 199.99,
      originalPrice: 249.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400',
      rating: 4.3,
      reviews: 89,
      inStock: true
    },
    {
      id: 7,
      name: 'Wireless Earbuds',
      price: 79.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1590658165737-15a047b8b5e3?w=400',
      rating: 4.3,
      reviews: 189,
      inStock: false
    }
  ]);

  const { addToCart } = useCart();

  const removeFromWishlist = (id) => {
    setWishlist(wishlist.filter(item => item.id !== id));
  };

  const moveToCart = (product) => {
    addToCart(product);
    removeFromWishlist(product.id);
  };

  if (!wishlist || wishlist.length === 0) {
    return (
      <div className="wishlist-page">
        <div className="wishlist-container">
          <motion.div
            className="empty-wishlist"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <div className="empty-wishlist-icon">❤️</div>
            <h2>Your Wishlist is Empty</h2>
            <p>Save items you love to your wishlist and shop them later!</p>
            <Link to="/">
              <button className="btn btn-primary">Start Shopping</button>
            </Link>
          </motion.div>
        </div>
      </div>
    );
  }

  return (
    <div className="wishlist-page">
      <div className="wishlist-container">
        <motion.div
          className="wishlist-header"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <h1>My Wishlist</h1>
          <p>{wishlist.length} {wishlist.length === 1 ? 'item' : 'items'} saved</p>
        </motion.div>

        <div className="wishlist-grid">
          {wishlist.map((item, index) => (
            <motion.div
              key={item.id}
              className="wishlist-item"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              {!item.inStock && (
                <div className="out-of-stock-badge">Out of Stock</div>
              )}

              <button
                className="remove-wishlist-btn"
                onClick={() => removeFromWishlist(item.id)}
                title="Remove from wishlist"
              >
                ✕
              </button>

              <Link to={`/product/${item.id}`}>
                <div className="wishlist-item-image">
                  <img src={item.image} alt={item.name} />
                </div>
              </Link>

              <div className="wishlist-item-info">
                <div className="wishlist-item-category">{item.category}</div>
                <h3 className="wishlist-item-name">{item.name}</h3>
                
                <div className="wishlist-item-rating">
                  <div className="stars">{'⭐'.repeat(Math.floor(item.rating))}</div>
                  <span className="rating-count">({item.reviews})</span>
                </div>

                <div className="wishlist-item-price">
                  <span className="price">${item.price}</span>
                  {item.originalPrice && (
                    <span className="original-price">${item.originalPrice}</span>
                  )}
                </div>

                {item.originalPrice && (
                  <div className="savings">
                    Save ${(item.originalPrice - item.price).toFixed(2)} (
                    {Math.round(((item.originalPrice - item.price) / item.originalPrice) * 100)}% off)
                  </div>
                )}

                <button
                  className={`move-to-cart-btn ${!item.inStock ? 'disabled' : ''}`}
                  onClick={() => moveToCart(item)}
                  disabled={!item.inStock}
                >
                  {item.inStock ? (
                    <>
                      <span>🛒</span>
                      <span>Move to Cart</span>
                    </>
                  ) : (
                    <>
                      <span>🔔</span>
                      <span>Notify Me</span>
                    </>
                  )}
                </button>
              </div>
            </motion.div>
          ))}
        </div>

        <motion.div
          className="wishlist-actions"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.3 }}
        >
          <button
            className="add-all-to-cart"
            onClick={() => {
              wishlist.filter(item => item.inStock).forEach(item => moveToCart(item));
            }}
          >
            Add All to Cart
          </button>
          <Link to="/">
            <button className="continue-shopping">Continue Shopping</button>
          </Link>
        </motion.div>
      </div>
    </div>
  );
};

export default Wishlist;
