import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useCart } from '../context/CartContext';

const CartPage = () => {
  const { cartItems, removeFromCart, updateQuantity, getCartTotal, clearCart } = useCart();
  const navigate = useNavigate();
  const [removingItems, setRemovingItems] = useState([]);
  const [showClearConfirm, setShowClearConfirm] = useState(false);
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [couponCode, setCouponCode] = useState('');
  const [showCouponInput, setShowCouponInput] = useState(false);

  // Handle remove with animation
  const handleRemove = (itemId) => {
    setRemovingItems([...removingItems, itemId]);
    setTimeout(() => {
      removeFromCart(itemId);
      setRemovingItems(removingItems.filter(id => id !== itemId));
    }, 300);
  };

  // Handle coupon apply
  const handleApplyCoupon = () => {
    const code = couponCode.toUpperCase();
    if (code === 'SAVE10') {
      setAppliedCoupon({ code: 'SAVE10', discount: 10 });
    } else if (code === 'WELCOME20') {
      setAppliedCoupon({ code: 'WELCOME20', discount: 20 });
    } else {
      alert('❌ Invalid coupon code! Try SAVE10 or WELCOME20');
      return;
    }
    setCouponCode('');
    setShowCouponInput(false);
  };

  // Empty cart state
  if (!cartItems || cartItems.length === 0) {
    return (
      <motion.div 
        className="empty-cart-page"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        <div className="empty-cart-content">
          <motion.div
            className="empty-cart-icon"
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ type: 'spring', stiffness: 200, damping: 15 }}
          >
            🛒
          </motion.div>
          <motion.h2
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.2 }}
          >
            Your Cart is Empty
          </motion.h2>
          <motion.p
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.3 }}
          >
            Looks like you haven't added anything yet. Start shopping now!
          </motion.p>
          
          <motion.div
            className="empty-suggestions"
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.4 }}
          >
            <div className="suggestion-item">
              <span>🔥</span>
              <p>Trending Products</p>
            </div>
            <div className="suggestion-item">
              <span>⭐</span>
              <p>Best Sellers</p>
            </div>
            <div className="suggestion-item">
              <span>🎁</span>
              <p>Special Offers</p>
            </div>
          </motion.div>

          <Link to="/">
            <motion.button
              className="continue-shopping-btn"
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.5 }}
              whileHover={{ scale: 1.05, boxShadow: '0 10px 40px rgba(102, 126, 234, 0.3)' }}
              whileTap={{ scale: 0.95 }}
            >
              <span>🛍️</span>
              <span>Start Shopping</span>
            </motion.button>
          </Link>
        </div>
      </motion.div>
    );
  }

  // Calculate totals
  const subtotal = getCartTotal();
  const shipping = subtotal > 50 ? 0 : 5.99;
  const tax = subtotal * 0.08;
  const couponDiscount = appliedCoupon ? (subtotal * appliedCoupon.discount) / 100 : 0;
  const total = subtotal + shipping + tax - couponDiscount;

  return (
    <motion.div 
      className="cart-page"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <div className="cart-container">
        {/* Breadcrumb */}
        <motion.nav 
          className="cart-breadcrumb"
          initial={{ y: -10, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
        >
          <Link to="/">Home</Link>
          <span>/</span>
          <span>Shopping Cart</span>
        </motion.nav>

        {/* Header */}
        <motion.div 
          className="cart-header"
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.1 }}
        >
          <div className="cart-title-section">
            <h1>Shopping Cart</h1>
            <motion.p 
              className="cart-count"
              key={cartItems.length}
              initial={{ scale: 1.3 }}
              animate={{ scale: 1 }}
            >
              {cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}
            </motion.p>
          </div>
          <motion.button
            className="clear-cart-btn"
            onClick={() => setShowClearConfirm(true)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <span>🗑️</span>
            <span>Clear All</span>
          </motion.button>
        </motion.div>

        {/* Free Shipping Progress */}
        <motion.div 
          className="free-shipping-progress"
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          {subtotal < 50 ? (
            <>
              <div className="progress-bar-container">
                <motion.div 
                  className="progress-bar-fill"
                  initial={{ width: 0 }}
                  animate={{ width: `${Math.min((subtotal / 50) * 100, 100)}%` }}
                  transition={{ duration: 0.8, ease: 'easeOut' }}
                />
              </div>
              <p className="progress-text">
                🚚 Add <strong>${(50 - subtotal).toFixed(2)}</strong> more to unlock <strong>FREE SHIPPING!</strong>
              </p>
            </>
          ) : (
            <motion.p 
              className="free-shipping-achieved"
              initial={{ scale: 0.8 }}
              animate={{ scale: 1 }}
            >
              🎉 Congratulations! You've unlocked <strong>FREE SHIPPING!</strong>
            </motion.p>
          )}
        </motion.div>

        <div className="cart-content-grid">
          {/* Cart Items Section */}
          <div className="cart-items-section">
            {/* Table Header - Desktop Only */}
            <div className="cart-table-header">
              <span className="header-product">Product</span>
              <span className="header-price">Price</span>
              <span className="header-quantity">Quantity</span>
              <span className="header-total">Total</span>
              <span className="header-remove"></span>
            </div>

            {/* Cart Items */}
            <AnimatePresence mode="popLayout">
              {cartItems.map((item, index) => (
                <motion.div
                  key={item.id}
                  className={`cart-item ${removingItems.includes(item.id) ? 'removing' : ''}`}
                  layout
                  initial={{ opacity: 0, x: -50 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 100, height: 0, marginBottom: 0 }}
                  transition={{ 
                    layout: { duration: 0.3 },
                    delay: index * 0.05 
                  }}
                  whileHover={{ 
                    backgroundColor: 'rgba(102, 126, 234, 0.05)',
                    transition: { duration: 0.2 }
                  }}
                >
                  <div className="item-product-info">
                    <div className="item-image-wrapper">
                      <motion.img 
                        src={item.image} 
                        alt={item.name}
                        whileHover={{ scale: 1.1 }}
                        transition={{ duration: 0.3 }}
                      />
                    </div>
                    <div className="item-details">
                      <h3>{item.name}</h3>
                      <p className="item-category">{item.category}</p>
                    </div>
                  </div>

                  <div className="item-price">
                    <span className="mobile-label">Price:</span>
                    <span className="price-value">${item.price.toFixed(2)}</span>
                  </div>

                  <div className="item-quantity">
                    <span className="mobile-label">Quantity:</span>
                    <div className="quantity-controls">
                      <motion.button
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                        whileHover={{ scale: 1.2 }}
                        whileTap={{ scale: 0.9 }}
                        disabled={item.quantity <= 1}
                      >
                        −
                      </motion.button>
                      <motion.span 
                        key={item.quantity}
                        initial={{ scale: 1.3 }}
                        animate={{ scale: 1 }}
                        className="quantity-display"
                      >
                        {item.quantity}
                      </motion.span>
                      <motion.button
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                        whileHover={{ scale: 1.2 }}
                        whileTap={{ scale: 0.9 }}
                      >
                        +
                      </motion.button>
                    </div>
                  </div>

                  <div className="item-total">
                    <span className="mobile-label">Total:</span>
                    <motion.span 
                      className="total-value"
                      key={item.quantity * item.price}
                      initial={{ scale: 1.2 }}
                      animate={{ scale: 1 }}
                    >
                      ${(item.price * item.quantity).toFixed(2)}
                    </motion.span>
                  </div>

                  <motion.button 
                    className="remove-item-btn"
                    onClick={() => handleRemove(item.id)}
                    whileHover={{ scale: 1.2, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                    title="Remove item"
                  >
                    ✕
                  </motion.button>
                </motion.div>
              ))}
            </AnimatePresence>
          </div>

          {/* Order Summary */}
          <motion.div 
            className="cart-summary-sticky"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: 0.3 }}
          >
            <div className="cart-summary">
              <div className="summary-header">
                <h2>Order Summary</h2>
              </div>

              {/* Coupon Section */}
              <div className="coupon-section">
                <motion.button
                  className="show-coupon-btn"
                  onClick={() => setShowCouponInput(!showCouponInput)}
                  whileHover={{ scale: 1.02 }}
                >
                  <span>🎁</span>
                  <span>Have a Coupon Code?</span>
                  <span className="toggle-icon">{showCouponInput ? '−' : '+'}</span>
                </motion.button>

                <AnimatePresence>
                  {showCouponInput && (
                    <motion.div
                      className="coupon-input-wrapper"
                      initial={{ height: 0, opacity: 0 }}
                      animate={{ height: 'auto', opacity: 1 }}
                      exit={{ height: 0, opacity: 0 }}
                    >
                      <div className="coupon-input-group">
                        <input
                          type="text"
                          placeholder="Enter code"
                          value={couponCode}
                          onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
                          onKeyPress={(e) => e.key === 'Enter' && handleApplyCoupon()}
                        />
                        <motion.button
                          onClick={handleApplyCoupon}
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          disabled={!couponCode}
                        >
                          Apply
                        </motion.button>
                      </div>
                      <div className="coupon-suggestions">
                        <small>💡 Try: <strong>SAVE10</strong> or <strong>WELCOME20</strong></small>
                      </div>
                    </motion.div>
                  )}
                </AnimatePresence>

                {appliedCoupon && (
                  <motion.div 
                    className="applied-coupon"
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                  >
                    <span>✓ {appliedCoupon.code} applied (-{appliedCoupon.discount}%)</span>
                    <motion.button 
                      onClick={() => setAppliedCoupon(null)}
                      whileHover={{ scale: 1.1 }}
                    >
                      ✕
                    </motion.button>
                  </motion.div>
                )}
              </div>

              <div className="summary-divider" />

              {/* Summary Details */}
              <div className="summary-details">
                <div className="summary-row">
                  <span>Subtotal</span>
                  <span>${subtotal.toFixed(2)}</span>
                </div>
                
                <div className="summary-row">
                  <span>Shipping</span>
                  <span className={shipping === 0 ? 'free-badge' : ''}>
                    {shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}
                  </span>
                </div>

                <div className="summary-row">
                  <span>Tax (8%)</span>
                  <span>${tax.toFixed(2)}</span>
                </div>

                {appliedCoupon && (
                  <motion.div 
                    className="summary-row discount-row"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                  >
                    <span>Discount</span>
                    <span className="discount-amount">-${couponDiscount.toFixed(2)}</span>
                  </motion.div>
                )}
              </div>

              <div className="summary-divider" />

              <div className="summary-row total-row">
                <span>Total</span>
                <motion.span 
                  className="total-amount"
                  key={total}
                  initial={{ scale: 1.2 }}
                  animate={{ scale: 1 }}
                >
                  ${total.toFixed(2)}
                </motion.span>
              </div>

              {/* Action Buttons */}
              <div className="summary-actions">
                <motion.button
                  className="checkout-btn"
                  onClick={() => navigate('/checkout')}
                  whileHover={{ scale: 1.03, boxShadow: '0 15px 50px rgba(102, 126, 234, 0.4)' }}
                  whileTap={{ scale: 0.98 }}
                >
                  <span>🔒</span>
                  <span>Proceed to Checkout</span>
                </motion.button>

                <Link to="/">
                  <motion.button 
                    className="continue-shopping-link"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    ← Continue Shopping
                  </motion.button>
                </Link>
              </div>

              {/* Trust Badges */}
              <div className="trust-badges">
                <div className="trust-item">
                  <span>🔒</span>
                  <div>
                    <strong>Secure Payment</strong>
                    <small>SSL Encrypted</small>
                  </div>
                </div>
                <div className="trust-item">
                  <span>📦</span>
                  <div>
                    <strong>Fast Delivery</strong>
                    <small>2-4 Days</small>
                  </div>
                </div>
                <div className="trust-item">
                  <span>↩️</span>
                  <div>
                    <strong>Easy Returns</strong>
                    <small>30-Day Policy</small>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Clear Cart Modal */}
      <AnimatePresence>
        {showClearConfirm && (
          <>
            <motion.div
              className="modal-overlay"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
              onClick={() => setShowClearConfirm(false)}
            />
            <motion.div
              className="confirm-modal"
              initial={{ opacity: 0, scale: 0.8, y: -50 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.8, y: -50 }}
            >
              <div className="modal-icon">🗑️</div>
              <h3>Clear Shopping Cart?</h3>
              <p>Remove all {cartItems.length} items from your cart?</p>
              <div className="modal-actions">
                <motion.button
                  className="btn-cancel"
                  onClick={() => setShowClearConfirm(false)}
                  whileHover={{ scale: 1.05 }}
                >
                  Cancel
                </motion.button>
                <motion.button
                  className="btn-confirm"
                  onClick={() => {
                    clearCart();
                    setShowClearConfirm(false);
                  }}
                  whileHover={{ scale: 1.05 }}
                >
                  Clear Cart
                </motion.button>
              </div>
            </motion.div>
          </>
        )}
      </AnimatePresence>
    </motion.div>
  );
};

export default CartPage;
