import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import AuthModal from './AuthModal';

const Header = () => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [isScrolled, setIsScrolled] = useState(false);
  const { getCartItemsCount } = useCart();
  const navigate = useNavigate();
  const location = useLocation();

  // Handle scroll effect
  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Close mobile menu on route change
  useEffect(() => {
    setIsMobileMenuOpen(false);
  }, [location]);

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate(`/?search=${searchQuery}`);
      setSearchQuery('');
    }
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <>
      <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
        <div className="container">
          <div className="header-content">
            {/* Mobile Menu Button */}
            <button 
              className={`mobile-menu-btn ${isMobileMenuOpen ? 'active' : ''}`}
              onClick={toggleMobileMenu}
            >
              <span></span>
              <span></span>
              <span></span>
            </button>

            {/* Logo */}
            <Link to="/" className="logo" onClick={() => setIsMobileMenuOpen(false)}>
              <span className="logo-icon">üõçÔ∏è</span>
              <span className="logo-text">ShopEasy</span>
            </Link>

            {/* Navigation */}
            <nav className="desktop-nav">
              <Link to="/" className="nav-link">Home</Link>
              <Link to="/products" className="nav-link">Products</Link>
              <Link to="/categories" className="nav-link">Categories</Link>
              <Link to="/deals" className="nav-link">Deals</Link>
            </nav>

            {/* Search Bar */}
            <form onSubmit={handleSearch} className="search-bar">
              <div className="search-container">
                <input
                  type="text"
                  placeholder="Search products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="search-input"
                />
                <button type="submit" className="search-btn">
                  <span className="search-icon">üîç</span>
                </button>
              </div>
            </form>

            {/* Right Side Actions */}
            <div className="header-actions">
              <button 
                className="auth-btn"
                onClick={() => setIsAuthModalOpen(true)}
              >
                <span className="auth-icon">üë§</span>
                <span className="auth-text">Login</span>
              </button>
              
              <Link to="/cart" className="cart-btn">
                <span className="cart-icon">üõí</span>
                {getCartItemsCount() > 0 && (
                  <span className="cart-count">{getCartItemsCount()}</span>
                )}
              </Link>
            </div>
          </div>

          {/* Mobile Navigation */}
          <div className={`mobile-nav ${isMobileMenuOpen ? 'active' : ''}`}>
            <nav className="mobile-nav-menu">
              <Link to="/" className="mobile-nav-link" onClick={toggleMobileMenu}>Home</Link>
              <Link to="/products" className="mobile-nav-link" onClick={toggleMobileMenu}>Products</Link>
              <Link to="/categories" className="mobile-nav-link" onClick={toggleMobileMenu}>Categories</Link>
              <Link to="/deals" className="mobile-nav-link" onClick={toggleMobileMenu}>Deals</Link>
              <Link to="/cart" className="mobile-nav-link" onClick={toggleMobileMenu}>
                Cart {getCartItemsCount() > 0 && `(${getCartItemsCount()})`}
              </Link>
              <button 
                className="mobile-nav-link auth-mobile"
                onClick={() => {
                  setIsAuthModalOpen(true);
                  toggleMobileMenu();
                }}
              >
                Login / Register
              </button>
            </nav>
          </div>
        </div>
      </header>

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div className="mobile-overlay" onClick={toggleMobileMenu}></div>
      )}

      <AuthModal 
        isOpen={isAuthModalOpen}
        onClose={() => setIsAuthModalOpen(false)}
      />
    </>
  );
};

export default Header;
