      rating: 4.5,
      reviews: 156,
      description: 'Stylish sunglasses with UV protection.',
      inStock: true,
      isNew: false,
      isFeatured: false
    }
  ];

  const categories = [
    { id: 'all', name: 'üåü All Products', count: mockProducts.length },
    { id: 'electronics', name: 'üéß Electronics', count: mockProducts.filter(p => p.category === 'electronics').length },
    { id: 'clothing', name: 'üëï Clothing', count: mockProducts.filter(p => p.category === 'clothing').length },
    { id: 'footwear', name: 'üëü Footwear', count: mockProducts.filter(p => p.category === 'footwear').length },
    { id: 'home', name: 'üè† Home', count: mockProducts.filter(p => p.category === 'home').length },
    { id: 'accessories', name: 'üëú Accessories', count: mockProducts.filter(p => p.category === 'accessories').length }
  ];

  const sortOptions = [
    { value: 'featured', label: '‚≠ê Featured' },
    { value: 'price-low', label: 'üí∞ Price: Low to High' },
    { value: 'price-high', label: 'üí∏ Price: High to Low' },
    { value: 'rating', label: '‚≠ê Highest Rated' },
    { value: 'newest', label: 'üÜï Newest' }
  ];

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1500));
      setProducts(mockProducts);
      setFilteredProducts(mockProducts);
      setLoading(false);
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    let filtered = [...products];

    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    if (searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    filtered = filtered.filter(product => product.price >= priceRange[0] && product.price <= priceRange[1]);

    switch (sortBy) {
      case 'price-low': filtered.sort((a, b) => a.price - b.price); break;
      case 'price-high': filtered.sort((a, b) => b.price - a.price); break;
      case 'rating': filtered.sort((a, b) => b.rating - a.rating); break;
      case 'newest': filtered.sort((a, b) => (b.isNew ? 1 : 0) - (a.isNew ? 1 : 0)); break;
      default: filtered.sort((a, b) => (b.isFeatured ? 1 : 0) - (a.isFeatured ? 1 : 0));
    }

    setFilteredProducts(filtered);
  }, [selectedCategory, searchQuery, products, sortBy, priceRange]);

  const resetFilters = () => {
    setSelectedCategory('all');
    setSortBy('featured');
    setPriceRange([0, 500]);
  };

  if (loading) return <LoadingSpinner />;

  return (
    <>
      {/* Hero Section */}
      <motion.section className="hero-section" ref={heroRef}>
        <div className="container">
          <motion.div
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: heroInView ? 1 : 0, x: heroInView ? 0 : -50 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <h1 className="hero-title">
              Discover Amazing
              <span className="highlight">Deals Today!</span>
            </h1>
            <p className="hero-subtitle">Shop the latest trends and best deals in our curated collection. Quality products at unbeatable prices.</p>
            <div className="hero-actions">
              <button className="btn btn-primary">Shop Now üõçÔ∏è</button>
              <button className="btn btn-secondary">View Collections ‚ú®</button>
            </div>
          </motion.div>

          <motion.div className="hero-image" initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: heroInView ? 1 : 0, scale: heroInView ? 1 : 0.8 }}>
            <motion.div className="floating-card card-1" whileHover={{ scale: 1.1, rotate: 5 }}>
              <span>üéß</span>
              <div>Audio</div>
            </motion.div>
            <motion.div className="floating-card card-2" whileHover={{ scale: 1.1, rotate: -5 }}>
              <span>‚åö</span>
              <div>Wearables</div>
            </motion.div>
            <motion.div className="floating-card card-3" whileHover={{ scale: 1.1, rotate: 5 }}>
              <span>üëü</span>
              <div>Footwear</div>
            </motion.div>
          </motion.div>
        </div>
      </motion.section>

      {/* Filters Section */}
      <motion.section className="filters-section" ref={filtersRef}>
        <div className="container">
          <div className="section-header">
            <h2>Our Products</h2>
            <span className="results-count">Showing {filteredProducts.length} products</span>
          </div>

          <div className="filters-toolbar">
            <div className="category-filters">
              {categories.map((category, index) => (
                <motion.button
                  key={category.id}
                  className={`category-btn ${selectedCategory === category.id ? 'active' : ''}`}
                  onClick={() => setSelectedCategory(category.id)}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {category.name}
                  <span className="category-count">{category.count}</span>
                </motion.button>
              ))}
            </div>

            <div className="filter-controls">
              <div className="filter-item">
                <label>Price Range: ${priceRange[0]} - ${priceRange[1]}</label>
                <input type="range" className="range-slider" min="0" max="500" value={priceRange[1]} onChange={(e) => setPriceRange([0, parseInt(e.target.value)])} />
              </div>

              <div className="filter-item">
                <label>Sort By</label>
                <select className="sort-select" value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                  {sortOptions.map(option => <option key={option.value} value={option.value}>{option.label}</option>)}
                </select>
              </div>

              <motion.button className="reset-filters" onClick={resetFilters} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
                üîÑ Reset Filters
              </motion.button>
            </div>
          </div>
        </div>
      </motion.section>

      {/* Products Grid */}
      <section className="products-section">
        <div className="container">
          {filteredProducts.length > 0 ? (
            <div className="products-grid">
              {filteredProducts.map((product) => <ProductCard key={product.id} product={product} />)}
            </div>
          ) : (
            <motion.div className="no-products" initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }}>
              <div className="no-products-icon">üòï</div>
              <h3>No Products Found</h3>
              <p>Try adjusting your search criteria or filters</p>
              <button className="btn btn-primary" onClick={resetFilters}>Reset Filters</button>
            </motion.div>
          )}
        </div>
      </section>

      {/* Features Section */}
      <motion.section className="features-section" initial={{ opacity: 0 }} whileInView={{ opacity: 1 }} viewport={{ once: true }}>
        <div className="container">
          <div className="features-grid">
            {[
              { icon: 'üöö', title: 'Free Delivery', desc: 'Free delivery on orders over $50' },
              { icon: '‚Ü©Ô∏è', title: 'Easy Returns', desc: '7-day return policy' },
              { icon: 'üîí', title: 'Secure Payment', desc: 'Your payment information is safe' },
              { icon: 'üí¨', title: '24/7 Support', desc: 'Get help whenever you need' }
            ].map((feature, index) => (
              <motion.div key={index} className="feature-card" whileHover={{ y: -10 }}>
                <div className="feature-icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.desc}</p>
              </motion.div>
            ))}
          </div>
        </div>
      </motion.section>
    </>
  );
};

export default Home;
