import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axios from 'axios';
import '../styles/Add.css';

const AdminDashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('overview');
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [stats, setStats] = useState({
    totalOrders: 0,
    totalRevenue: 0,
    totalProducts: 0,
    pendingOrders: 0,
    totalUsers: 0,
    activeUsers: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      navigate('/login');
      return;
    }
    fetchDashboardData();
  }, [user, navigate]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('authToken');
      const config = { headers: { 'Authorization': `Bearer ${token}` } };

      const [ordersRes, productsRes, usersRes, statsRes] = await Promise.all([
        axios.get('http://localhost:5000/api/orders', config),
        axios.get('http://localhost:5000/api/products', config),
        axios.get('http://localhost:5000/api/users', config),
        axios.get('http://localhost:5000/api/admin/stats', config)
      ]);

      setOrders(ordersRes.data);
      setProducts(productsRes.data);
      setUsers(usersRes.data);
      setStats(statsRes.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;

    try {
      const token = localStorage.getItem('authToken');
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      alert('User deleted successfully');
      fetchDashboardData();
    } catch (error) {
      console.error('Error deleting user:', error);
      alert('Failed to delete user');
    }
  };

  const handleUpdateOrderStatus = async (orderId, newStatus, stepIndex) => {
    try {
      const token = localStorage.getItem('authToken');
      await axios.patch(
        `http://localhost:5000/api/orders/${orderId}/status`,
        { status: newStatus, stepIndex },
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      alert('Order status updated!');
      fetchDashboardData();
    } catch (error) {
      console.error('Error updating order:', error);
      alert('Failed to update order status');
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) {
    return (
      <div className="admin-loading">
        <div className="loading-spinner"></div>
        <p>Loading Dashboard...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* Sidebar */}
      <aside className="admin-sidebar">
        <div className="admin-brand">
          <h2>🛍️ ShopHub Admin</h2>
          <p>Welcome, {user?.name}</p>
        </div>

        <nav className="admin-nav">
          <button
            className={`nav-item ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            <span className="nav-icon">📊</span>
            <span>Overview</span>
          </button>
          <button
            className={`nav-item ${activeTab === 'orders' ? 'active' : ''}`}
            onClick={() => setActiveTab('orders')}
          >
            <span className="nav-icon">📦</span>
            <span>Orders</span>
            {stats.pendingOrders > 0 && (
              <span className="badge">{stats.pendingOrders}</span>
            )}
          </button>
          <button
            className={`nav-item ${activeTab === 'products' ? 'active' : ''}`}
            onClick={() => setActiveTab('products')}
          >
            <span className="nav-icon">🛒</span>
            <span>Products</span>
          </button>
          <button
            className={`nav-item ${activeTab === 'users' ? 'active' : ''}`}
            onClick={() => setActiveTab('users')}
          >
            <span className="nav-icon">👥</span>
            <span>Users</span>
            <span className="badge">{stats.totalUsers}</span>
          </button>
        </nav>

        <button className="logout-btn" onClick={handleLogout}>
          <span>🚪</span>
          <span>Logout</span>
        </button>
      </aside>

      {/* Main Content */}
      <main className="admin-main">
        <AnimatePresence mode="wait">
          {/* Overview Tab */}
          {activeTab === 'overview' && (
            <motion.div
              key="overview"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className="admin-content"
            >
              <h1 className="page-title">Dashboard Overview</h1>

              {/* Stats Grid */}
              <div className="stats-grid">
                <motion.div className="stat-card" whileHover={{ y: -5 }}>
                  <div className="stat-icon total-orders">📦</div>
                  <div className="stat-info">
                    <h3>Total Orders</h3>
                    <p className="stat-value">{stats.totalOrders}</p>
                  </div>
                </motion.div>

                <motion.div className="stat-card" whileHover={{ y: -5 }}>
                  <div className="stat-icon total-revenue">💰</div>
                  <div className="stat-info">
                    <h3>Total Revenue</h3>
                    <p className="stat-value">${stats.totalRevenue.toFixed(2)}</p>
                  </div>
                </motion.div>

                <motion.div className="stat-card" whileHover={{ y: -5 }}>
                  <div className="stat-icon total-products">🛒</div>
                  <div className="stat-info">
                    <h3>Total Products</h3>
                    <p className="stat-value">{stats.totalProducts}</p>
                  </div>
                </motion.div>

                <motion.div className="stat-card" whileHover={{ y: -5 }}>
                  <div className="stat-icon pending-orders">⏳</div>
                  <div className="stat-info">
                    <h3>Pending Orders</h3>
                    <p className="stat-value">{stats.pendingOrders}</p>
                  </div>
                </motion.div>

                <motion.div className="stat-card" whileHover={{ y: -5 }}>
                  <div className="stat-icon total-users">👥</div>
                  <div className="stat-info">
                    <h3>Total Users</h3>
                    <p className="stat-value">{stats.totalUsers}</p>
                  </div>
                </motion.div>

                <motion.div className="stat-card" whileHover={{ y: -5 }}>
                  <div className="stat-icon active-users">✅</div>
                  <div className="stat-info">
                    <h3>Active Users (30d)</h3>
                    <p className="stat-value">{stats.activeUsers}</p>
                  </div>
                </motion.div>
              </div>

              {/* Recent Orders */}
              <div className="recent-section">
                <h2>Recent Orders</h2>
                <div className="orders-table-container">
                  <table className="orders-table">
                    <thead>
                      <tr>
                        <th>Order #</th>
                        <th>Customer</th>
                        <th>Date</th>
                        <th>Total</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {orders.slice(0, 5).map((order) => (
                        <tr key={order._id}>
                          <td>#{order.orderNumber}</td>
                          <td>{order.shippingInfo?.name || 'N/A'}</td>
                          <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                          <td>${order.total?.toFixed(2)}</td>
                          <td>
                            <span className={`status-badge ${order.status}`}>
                              {order.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </motion.div>
          )}

          {/* Orders Tab */}
          {activeTab === 'orders' && (
            <motion.div
              key="orders"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className="admin-content"
            >
              <h1 className="page-title">Orders Management</h1>
              <div className="orders-list">
                {orders.map((order) => (
                  <motion.div
                    key={order._id}
                    className="order-card"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                  >
                    <div className="order-header">
                      <h3>Order #{order.orderNumber}</h3>
                      <select
                        value={order.status}
                        onChange={(e) => handleUpdateOrderStatus(order._id, e.target.value)}
                        className="status-select"
                      >
                        <option value="pending">Pending</option>
                        <option value="processing">Processing</option>
                        <option value="shipped">Shipped</option>
                        <option value="delivered">Delivered</option>
                        <option value="cancelled">Cancelled</option>
                      </select>
                    </div>
                    <div className="order-details">
                      <p><strong>Customer:</strong> {order.shippingInfo?.name}</p>
                      <p><strong>Email:</strong> {order.shippingInfo?.email}</p>
                      <p><strong>Total:</strong> ${order.total?.toFixed(2)}</p>
                      <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
                    </div>
                    <div className="order-items">
                      <h4>Items:</h4>
                      {order.items?.map((item, idx) => (
                        <p key={idx}>
                          {item.name} x {item.quantity} - ${(item.price * item.quantity).toFixed(2)}
                        </p>
                      ))}
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          )}

          {/* Products Tab */}
          {activeTab === 'products' && (
            <motion.div
              key="products"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className="admin-content"
            >
              <h1 className="page-title">Products Management</h1>
              <div className="products-grid">
                {products.map((product) => (
                  <motion.div
                    key={product._id}
                    className="product-admin-card"
                    whileHover={{ y: -5 }}
                  >
                    <img src={product.image} alt={product.name} />
                    <h3>{product.name}</h3>
                    <p className="product-category">{product.category}</p>
                    <p className="product-price">${product.price?.toFixed(2)}</p>
                    <p className="product-stock">Stock: {product.stock || 0}</p>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          )}

          {/* Users Tab - NEW */}
          {activeTab === 'users' && (
            <motion.div
              key="users"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              className="admin-content"
            >
              <h1 className="page-title">Users Management</h1>
              
              <div className="users-stats">
                <div className="stat-box">
                  <h3>Total Users</h3>
                  <p className="stat-number">{stats.totalUsers}</p>
                </div>
                <div className="stat-box">
                  <h3>Active Users (30 days)</h3>
                  <p className="stat-number">{stats.activeUsers}</p>
                </div>
                <div className="stat-box">
                  <h3>Admin Users</h3>
                  <p className="stat-number">{users.filter(u => u.role === 'admin').length}</p>
                </div>
              </div>

              <div className="users-table-container">
                <table className="users-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Role</th>
                      <th>Phone</th>
                      <th>Joined Date</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {users.map((userData) => (
                      <tr key={userData._id}>
                        <td>{userData.name}</td>
                        <td>{userData.email}</td>
                        <td>
                          <span className={`role-badge ${userData.role}`}>
                            {userData.role === 'admin' ? '👑 Admin' : '👤 User'}
                          </span>
                        </td>
                        <td>{userData.phone || 'N/A'}</td>
                        <td>{new Date(userData.createdAt).toLocaleDateString()}</td>
                        <td>
                          {userData.role !== 'admin' && (
                            <motion.button
                              className="delete-user-btn"
                              onClick={() => handleDeleteUser(userData._id)}
                              whileHover={{ scale: 1.05 }}
                              whileTap={{ scale: 0.95 }}
                            >
                              🗑️ Delete
                            </motion.button>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </motion.div>
          )}
        </AnimatePresence>
      </main>
    </div>
  );
};

export default AdminDashboard;
