import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useCart } from '../context/CartContext';
import { useTheme } from '../context/ThemeContext';

const CartPage = () => {
  const { cartItems, removeFromCart, updateQuantity, getCartTotal, clearCart } = useCart();
  const { isDarkMode } = useTheme();
  const navigate = useNavigate();
  
  const [removingItems, setRemovingItems] = useState([]);
  const [showClearConfirm, setShowClearConfirm] = useState(false);
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [couponCode, setCouponCode] = useState('');
  const [showCouponInput, setShowCouponInput] = useState(false);

  // Handle remove with animation
  const handleRemove = (itemId) => {
    setRemovingItems([...removingItems, itemId]);
    setTimeout(() => {
      removeFromCart(itemId);
      setRemovingItems(removingItems.filter(id => id !== itemId));
    }, 300);
  };

  // Handle coupon apply
  const handleApplyCoupon = () => {
    const code = couponCode.toUpperCase();
    if (code === 'SAVE10') {
      setAppliedCoupon({ code: 'SAVE10', discount: 10 });
    } else if (code === 'WELCOME20') {
      setAppliedCoupon({ code: 'WELCOME20', discount: 20 });
    } else {
      alert('‚ùå Invalid coupon code! Try SAVE10 or WELCOME20');
      return;
    }
    setCouponCode('');
    setShowCouponInput(false);
  };

  // Handle checkout navigation
  const handleCheckout = () => {
    navigate('/checkout');
  };

  // Empty cart state
  if (!cartItems || cartItems.length === 0) {
    return (
      <div className={`cart-page empty-cart ${isDarkMode ? 'dark-mode' : ''}`}>
        <div className="cart-container">
          <motion.div
            className="empty-cart-content"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
          >
            <div className="empty-icon">üõí</div>
            <h1>Your Cart is Empty</h1>
            <p>Looks like you haven't added anything yet. Start shopping now!</p>
            
            <div className="quick-links">
              <Link to="/?category=electronics">
                üì± Trending Products
              </Link>
              <Link to="/?category=fashion">
                üëï Best Sellers
              </Link>
              <Link to="/">
                üéÅ Special Offers
              </Link>
            </div>
            
            <Link to="/">
              <motion.button
                className="cta-button"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                üõçÔ∏è Start Shopping
              </motion.button>
            </Link>
          </motion.div>
        </div>
      </div>
    );
  }

  // Calculate totals
  const subtotal = getCartTotal();
  const shipping = subtotal > 50 ? 0 : 5.99;
  const tax = subtotal * 0.08;
  const couponDiscount = appliedCoupon ? (subtotal * appliedCoupon.discount) / 100 : 0;
  const total = subtotal + shipping + tax - couponDiscount;

  return (
    <div className={`cart-page ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className="cart-container">
        {/* Breadcrumb */}
        <div className="cart-breadcrumb">
          <Link to="/">Home</Link>
          <span>‚Ä∫</span>
          <span>Shopping Cart</span>
        </div>

        {/* Header */}
        <div className="cart-header">
          <div className="cart-title-section">
            <h1>Shopping Cart</h1>
            <p className="cart-count">
              {cartItems.length} {cartItems.length === 1 ? 'item' : 'items'}
            </p>
          </div>
          <motion.button
            className="clear-cart-btn"
            onClick={() => setShowClearConfirm(true)}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            üóëÔ∏è Clear All
          </motion.button>
        </div>

        {/* Free Shipping Progress */}
        <div className="free-shipping-progress">
          {subtotal < 50 ? (
            <>
              <div className="progress-bar-container">
                <motion.div
                  className="progress-bar-fill"
                  initial={{ width: 0 }}
                  animate={{ width: `${(subtotal / 50) * 100}%` }}
                  transition={{ duration: 0.8 }}
                />
              </div>
              <p className="progress-text">
                üöö Add <strong>${(50 - subtotal).toFixed(2)}</strong> more to unlock <strong>FREE SHIPPING!</strong>
              </p>
            </>
          ) : (
            <p className="free-shipping-achieved">
              üéâ Congratulations! You've unlocked FREE SHIPPING!
            </p>
          )}
        </div>

        {/* Cart Content Grid */}
        <div className="cart-content-grid">
          {/* Cart Items Section */}
          <div className="cart-items-section">
            {/* Table Header - Desktop Only */}
            <div className="cart-table-header">
              <div>Product</div>
              <div>Price</div>
              <div>Quantity</div>
              <div>Total</div>
              <div></div>
            </div>

            {/* Cart Items */}
            {cartItems.map((item, index) => (
              <motion.div
                key={item.id}
                className={`cart-item ${removingItems.includes(item.id) ? 'removing' : ''}`}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: 20 }}
                transition={{ delay: index * 0.1 }}
              >
                {/* Product Info */}
                <div className="item-product-info">
                  <div className="item-image-wrapper">
                    <img src={item.image} alt={item.name} />
                  </div>
                  <div className="item-details">
                    <h3>{item.name}</h3>
                    <span className="item-category">{item.category}</span>
                  </div>
                </div>

                {/* Price */}
                <div className="item-price">
                  <span className="mobile-label">Price:</span>
                  <span className="price-value">${item.price.toFixed(2)}</span>
                </div>

                {/* Quantity */}
                <div className="item-quantity">
                  <span className="mobile-label">Quantity:</span>
                  <div className="quantity-controls">
                    <motion.button
                      onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      whileHover={{ scale: 1.2 }}
                      whileTap={{ scale: 0.9 }}
                      disabled={item.quantity <= 1}
                    >
                      ‚àí
                    </motion.button>
                    <span className="quantity-display">{item.quantity}</span>
                    <motion.button
                      onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      whileHover={{ scale: 1.2 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      +
                    </motion.button>
                  </div>
                </div>

                {/* Total */}
                <div className="item-total">
                  <span className="mobile-label">Total:</span>
                  <span className="total-value">${(item.price * item.quantity).toFixed(2)}</span>
                </div>

                {/* Remove Button */}
                <motion.button
                  className="remove-item-btn"
                  onClick={() => handleRemove(item.id)}
                  whileHover={{ scale: 1.2, rotate: 90 }}
                  whileTap={{ scale: 0.9 }}
                  title="Remove item"
                >
                  ‚úï
                </motion.button>
              </motion.div>
            ))}
          </div>

          {/* Order Summary - Sticky */}
          <div className="cart-summary-sticky">
            <div className="cart-summary">
              <div className="summary-header">
                <h2>Order Summary</h2>
              </div>

              {/* Coupon Section */}
              <div className="coupon-section">
                <motion.button
                  className="show-coupon-btn"
                  onClick={() => setShowCouponInput(!showCouponInput)}
                  whileHover={{ scale: 1.02 }}
                >
                  <span>üéüÔ∏è Have a Coupon Code?</span>
                  <span className="toggle-icon">{showCouponInput ? '‚àí' : '+'}</span>
                </motion.button>

                <AnimatePresence>
                  {showCouponInput && (
                    <motion.div
                      className="coupon-input-wrapper"
                      initial={{ height: 0, opacity: 0 }}
                      animate={{ height: 'auto', opacity: 1 }}
                      exit={{ height: 0, opacity: 0 }}
                    >
                      <div className="coupon-input-group">
                        <input
                          type="text"
                          placeholder="Enter Code"
                          value={couponCode}
                          onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
                          onKeyPress={(e) => e.key === 'Enter' && handleApplyCoupon()}
                        />
                        <button onClick={handleApplyCoupon}>Apply</button>
                      </div>
                      <div className="coupon-suggestions">
                        <small>üí° Try: <strong>SAVE10</strong> or <strong>WELCOME20</strong></small>
                      </div>
                    </motion.div>
                  )}
                </AnimatePresence>

                {appliedCoupon && (
                  <motion.div
                    className="applied-coupon"
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                  >
                    <span>‚úì {appliedCoupon.code} applied (-{appliedCoupon.discount}%)</span>
                    <button
                      onClick={() => setAppliedCoupon(null)}
                      whileHover={{ scale: 1.1 }}
                    >
                      ‚úï
                    </button>
                  </motion.div>
                )}
              </div>

              {/* Summary Details */}
              <div className="summary-details">
                <div className="summary-row">
                  <span>Subtotal</span>
                  <strong>${subtotal.toFixed(2)}</strong>
                </div>
                <div className="summary-row">
                  <span>Shipping</span>
                  <strong>{shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}</strong>
                </div>
                <div className="summary-row">
                  <span>Tax (8%)</span>
                  <strong>${tax.toFixed(2)}</strong>
                </div>
                {appliedCoupon && (
                  <div className="summary-row discount-row">
                    <span>Discount</span>
                    <strong>-${couponDiscount.toFixed(2)}</strong>
                  </div>
                )}
                <div className="summary-row total-row">
                  <span>Total</span>
                  <strong>${total.toFixed(2)}</strong>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="summary-actions">
                <motion.button
                  className="checkout-btn"
                  onClick={handleCheckout}
                  whileHover={{ scale: 1.03, boxShadow: '0 15px 50px rgba(102, 126, 234, 0.4)' }}
                  whileTap={{ scale: 0.98 }}
                >
                  üí≥ Proceed to Checkout
                </motion.button>
                <Link to="/">
                  <button className="continue-shopping-btn">
                    ‚Üê Continue Shopping
                  </button>
                </Link>
              </div>

              {/* Trust Badges */}
              <div className="trust-badges">
                <div className="badge">
                  <span>üîí</span>
                  <div>
                    <strong>Secure Payment</strong>
                    <small>SSL Encrypted</small>
                  </div>
                </div>
                <div className="badge">
                  <span>üöö</span>
                  <div>
                    <strong>Fast Delivery</strong>
                    <small>2-4 Days</small>
                  </div>
                </div>
                <div className="badge">
                  <span>‚Ü©Ô∏è</span>
                  <div>
                    <strong>Easy Returns</strong>
                    <small>30-Day Policy</small>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Clear Cart Modal */}
        <AnimatePresence>
          {showClearConfirm && (
            <>
              <motion.div
                className="modal-overlay"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={() => setShowClearConfirm(false)}
              />
              <motion.div
                className="clear-cart-modal"
                initial={{ scale: 0.8, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                exit={{ scale: 0.8, opacity: 0 }}
              >
                <div className="modal-icon">üóëÔ∏è</div>
                <h3>Clear Shopping Cart?</h3>
                <p>Remove all {cartItems.length} items from your cart?</p>
                <div className="modal-actions">
                  <motion.button
                    className="btn-cancel"
                    onClick={() => setShowClearConfirm(false)}
                    whileHover={{ scale: 1.05 }}
                  >
                    Cancel
                  </motion.button>
                  <motion.button
                    className="btn-confirm"
                    onClick={() => {
                      clearCart();
                      setShowClearConfirm(false);
                    }}
                    whileHover={{ scale: 1.05 }}
                  >
                    Clear Cart
                  </motion.button>
                </div>
              </motion.div>
            </>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default CartPage;
