import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import ProductCard from '../components/ProductCard';

const Home = () => {
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [hoveredFeature, setHoveredFeature] = useState(null);
  const { addToCart } = useCart();

  // Mock products data
  const products = [
    {
      id: 1,
      name: 'Wireless Bluetooth Headphones',
      price: 99.99,
      originalPrice: 129.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
      rating: 4.5,
      reviews: 128,
      badge: 'hot'
    },
    {
      id: 2,
      name: 'Smart Fitness Watch',
      price: 199.99,
      originalPrice: 249.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400',
      rating: 4.3,
      reviews: 89,
      badge: 'new'
    },
    {
      id: 3,
      name: 'Premium Yoga Mat',
      price: 49.99,
      category: 'sports',
      image: 'https://images.unsplash.com/photo-1601925260368-ae2f83cf8b7f?w=400',
      rating: 4.7,
      reviews: 234,
      badge: null
    },
    {
      id: 4,
      name: 'Running Shoes',
      price: 129.99,
      category: 'footwear',
      image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=400',
      rating: 4.7,
      reviews: 342,
      badge: 'hot'
    },
    {
      id: 5,
      name: 'Laptop Backpack',
      price: 59.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400',
      rating: 4.4,
      reviews: 167,
      badge: null
    },
    {
      id: 6,
      name: 'Coffee Maker',
      price: 89.99,
      category: 'home',
      image: 'https://images.unsplash.com/photo-1517668808822-9ebb02f2a0e6?w=400',
      rating: 4.6,
      reviews: 198,
      badge: 'new'
    },
    {
      id: 7,
      name: 'Wireless Earbuds',
      price: 79.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1590658165737-15a047b8b5e3?w=400',
      rating: 4.3,
      reviews: 189,
      badge: 'hot'
    },
    {
      id: 8,
      name: 'Designer Sunglasses',
      price: 89.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1572635196237-14b3f281503f?w=400',
      rating: 4.5,
      reviews: 156,
      badge: null
    }
  ];

  const categories = [
    { id: 'all', name: 'All Products', icon: '🛍️', count: products.length },
    { id: 'electronics', name: 'Electronics', icon: '📱', count: products.filter(p => p.category === 'electronics').length },
    { id: 'sports', name: 'Sports', icon: '⚽', count: products.filter(p => p.category === 'sports').length },
    { id: 'footwear', name: 'Footwear', icon: '👟', count: products.filter(p => p.category === 'footwear').length },
    { id: 'accessories', name: 'Accessories', icon: '🎒', count: products.filter(p => p.category === 'accessories').length },
    { id: 'home', name: 'Home', icon: '🏠', count: products.filter(p => p.category === 'home').length }
  ];

  const features = [
    { icon: '🚚', title: 'Free Shipping', desc: 'On orders over $50', color: '#667eea' },
    { icon: '🔒', title: 'Secure Payment', desc: '100% secure transactions', color: '#f5576c' },
    { icon: '💳', title: 'Easy Returns', desc: '30-day return policy', color: '#4facfe' },
    { icon: '🎁', title: 'Gift Cards', desc: 'Perfect for any occasion', color: '#fbbf24' }
  ];

  const filteredProducts = selectedCategory === 'all' 
    ? products 
    : products.filter(p => p.category === selectedCategory);

  // Container variants for stagger effect
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 100
      }
    }
  };

  return (
    <div className="home-page">
      {/* Hero Section */}
      <motion.section 
        className="hero-section"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.8 }}
      >
        <div className="hero-container">
          <motion.div 
            className="hero-content"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <motion.div
              initial={{ scale: 0.5, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.5 }}
              className="hero-badge"
            >
              <span>🎉</span>
              <span>New Collection 2025</span>
            </motion.div>

            <motion.h1
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.3 }}
            >
              Discover Amazing
              <span className="hero-highlight"> Products</span>
              <br />
              <motion.span
                animate={{ 
                  backgroundPosition: ['0% 50%', '100% 50%', '0% 50%']
                }}
                transition={{ duration: 5, repeat: Infinity }}
                style={{
                  backgroundSize: '200% 200%',
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f5576c 100%)',
                  WebkitBackgroundClip: 'text',
                  backgroundClip: 'text',
                  WebkitTextFillColor: 'transparent'
                }}
              >
                Just For You
              </motion.span>
            </motion.h1>

            <motion.p
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.4 }}
            >
              Explore our curated collection of premium products at unbeatable prices. 
              Quality meets affordability in every item.
            </motion.p>

            <motion.div 
              className="hero-buttons"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.5 }}
            >
              <motion.button
                className="btn btn-primary"
                whileHover={{ scale: 1.05, boxShadow: '0 10px 40px rgba(102, 126, 234, 0.3)' }}
                whileTap={{ scale: 0.95 }}
                onClick={() => document.getElementById('products')?.scrollIntoView({ behavior: 'smooth' })}
              >
                <span>🛍️</span>
                <span>Shop Now</span>
              </motion.button>

              <motion.button
                className="btn btn-secondary"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <span>🎁</span>
                <span>View Deals</span>
              </motion.button>
            </motion.div>

            {/* Stats */}
            <motion.div 
              className="hero-stats"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: 0.6 }}
            >
              {[
                { num: '50K+', label: 'Products' },
                { num: '99%', label: 'Satisfaction' },
                { num: '24/7', label: 'Support' }
              ].map((stat, index) => (
                <motion.div 
                  key={index}
                  className="stat-item"
                  whileHover={{ scale: 1.1 }}
                >
                  <span className="stat-number">{stat.num}</span>
                  <span className="stat-label">{stat.label}</span>
                </motion.div>
              ))}
            </motion.div>
          </motion.div>

          <motion.div 
            className="hero-visual"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.3 }}
          >
            <AnimatePresence>
              {[1, 2, 3].map((card, index) => (
                <motion.div
                  key={card}
                  className={`floating-card card-${card}`}
                  initial={{ scale: 0, rotate: -180 }}
                  animate={{ 
                    scale: 1, 
                    rotate: 0,
                    y: [0, -20, 0]
                  }}
                  transition={{ 
                    duration: 0.8,
                    delay: 0.5 + index * 0.2,
                    y: {
                      duration: 3,
                      repeat: Infinity,
                      repeatType: 'reverse',
                      delay: index * 0.5
                    }
                  }}
                  whileHover={{ scale: 1.1, rotate: 5 }}
                >
                  <span>{['🎧', '⌚', '👟'][index]}</span>
                  <h4>{['Audio', 'Wearables', 'Fashion'][index]}</h4>
                  <p>{['Premium Sound', 'Smart Tech', 'Trending Now'][index]}</p>
                </motion.div>
              ))}
            </AnimatePresence>
          </motion.div>
        </div>
      </motion.section>

      {/* Features Section */}
      <motion.section 
        className="features-section"
        initial="hidden"
        whileInView="visible"
        viewport={{ once: true, amount: 0.3 }}
        variants={containerVariants}
      >
        <div className="features-container">
          {features.map((feature, index) => (
            <motion.div
              key={index}
              className="feature-card"
              variants={itemVariants}
              whileHover={{ 
                scale: 1.05,
                boxShadow: '0 20px 60px rgba(0,0,0,0.1)',
                backgroundColor: hoveredFeature === index ? feature.color + '10' : 'white'
              }}
              onHoverStart={() => setHoveredFeature(index)}
              onHoverEnd={() => setHoveredFeature(null)}
            >
              <motion.div 
                className="feature-icon"
                animate={{
                  scale: hoveredFeature === index ? [1, 1.2, 1] : 1
                }}
                transition={{ duration: 0.3 }}
                style={{ backgroundColor: feature.color + '20' }}
              >
                <span>{feature.icon}</span>
              </motion.div>
              <h3>{feature.title}</h3>
              <p>{feature.desc}</p>
            </motion.div>
          ))}
        </div>
      </motion.section>

      {/* Products Section */}
      <section className="products-section" id="products">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
        >
          <div className="section-header">
            <div>
              <h2>Featured Products</h2>
              <p className="results-count">Showing {filteredProducts.length} amazing products</p>
            </div>
          </div>

          {/* Category Filter */}
          <motion.div 
            className="category-filter"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
          >
            {categories.map((cat) => (
              <motion.button
                key={cat.id}
                className={`category-btn ${selectedCategory === cat.id ? 'active' : ''}`}
                onClick={() => setSelectedCategory(cat.id)}
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                layout
              >
                <span className="category-icon">{cat.icon}</span>
                <span className="category-name">{cat.name}</span>
                <motion.span 
                  className="category-count"
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ type: 'spring', stiffness: 200 }}
                >
                  {cat.count}
                </motion.span>
              </motion.button>
            ))}
          </motion.div>

          {/* Products Grid */}
          <AnimatePresence mode="wait">
            <motion.div
              key={selectedCategory}
              className="products-grid"
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              exit={{ opacity: 0, scale: 0.9 }}
            >
              {filteredProducts.map((product, index) => (
                <motion.div
                  key={product.id}
                  variants={itemVariants}
                  layout
                >
                  <ProductCard product={product} addToCart={addToCart} />
                </motion.div>
              ))}
            </motion.div>
          </AnimatePresence>

          {/* Empty State */}
          {filteredProducts.length === 0 && (
            <motion.div
              className="empty-products"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
            >
              <motion.div
                animate={{ rotate: [0, 10, -10, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <span className="empty-icon">🔍</span>
              </motion.div>
              <h3>No products found</h3>
              <p>Try selecting a different category</p>
            </motion.div>
          )}
        </motion.div>
      </section>

      {/* Newsletter Section */}
      <motion.section 
        className="newsletter-section"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true }}
        transition={{ duration: 0.8 }}
      >
        <div className="newsletter-container">
          <motion.div
            initial={{ scale: 0 }}
            whileInView={{ scale: 1 }}
            viewport={{ once: true }}
            transition={{ type: 'spring', stiffness: 100 }}
          >
            <span className="newsletter-icon">📧</span>
          </motion.div>
          <h2>Subscribe to Our Newsletter</h2>
          <p>Get exclusive deals and updates delivered straight to your inbox!</p>
          <motion.form 
            className="newsletter-form"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ delay: 0.3 }}
          >
            <motion.input
              type="email"
              placeholder="Enter your email..."
              whileFocus={{ scale: 1.02 }}
            />
            <motion.button
              type="submit"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span>Subscribe</span>
              <span>🚀</span>
            </motion.button>
          </motion.form>
        </div>
      </motion.section>
    </div>
  );
};

export default Home;
