import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import LoadingSpinner from '../components/LoadingSpinner';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [sortBy, setSortBy] = useState('featured');
  const [priceRange, setPriceRange] = useState([0, 1000]);
  const [searchParams] = useSearchParams();
  const searchQuery = searchParams.get('search');

  // Enhanced mock product data
  const mockProducts = [
    {
      id: 1,
      name: 'Wireless Bluetooth Headphones',
      price: 99.99,
      originalPrice: 129.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=400',
      images: [
        'https://images.unsplash.com/photo-1505740420928-5e560c06d30e?w=600',
        'https://images.unsplash.com/photo-1484704849700-f032a568e944?w=600'
      ],
      rating: 4.5,
      reviews: 128,
      description: 'High-quality wireless Bluetooth headphones with noise cancellation.',
      features: ['Active Noise Cancellation', '30-hour battery', 'Comfortable design'],
      inStock: true,
      isNew: true,
      isFeatured: true
    },
    {
      id: 2,
      name: 'Smart Fitness Watch',
      price: 199.99,
      originalPrice: 249.99,
      category: 'electronics',
      image: 'https://images.unsplash.com/photo-1523275335684-37898b6baf30?w=400',
      rating: 4.3,
      reviews: 89,
      description: 'Advanced fitness tracking with heart rate monitoring.',
      features: ['Heart Rate Monitor', 'GPS Tracking', 'Water Resistant'],
      inStock: true,
      isNew: false,
      isFeatured: true
    },
    {
      id: 3,
      name: 'Cotton T-Shirt',
      price: 24.99,
      category: 'clothing',
      image: 'https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?w=400',
      rating: 4.2,
      reviews: 256,
      description: 'Comfortable cotton t-shirt for everyday wear.',
      features: ['100% Cotton', 'Machine Washable', 'Multiple Colors'],
      inStock: true,
      isNew: true,
      isFeatured: false
    },
    {
      id: 4,
      name: 'Running Shoes',
      price: 129.99,
      category: 'footwear',
      image: 'https://images.unsplash.com/photo-1542291026-7eec264c27ff?w=400',
      rating: 4.7,
      reviews: 342,
      description: 'Lightweight running shoes with superior cushioning.',
      features: ['Lightweight', 'Breathable', 'Shock Absorption'],
      inStock: true,
      isNew: false,
      isFeatured: true
    },
    {
      id: 5,
      name: 'Coffee Maker',
      price: 79.99,
      category: 'home',
      image: 'https://images.unsplash.com/photo-1495474472287-4d71bcdd2085?w=400',
      rating: 4.4,
      reviews: 167,
      description: 'Programmable coffee maker for the perfect brew.',
      features: ['Programmable', '12-Cup Capacity', 'Auto Shut-off'],
      inStock: true,
      isNew: false,
      isFeatured: false
    },
    {
      id: 6,
      name: 'Backpack',
      price: 49.99,
      originalPrice: 69.99,
      category: 'accessories',
      image: 'https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400',
      rating: 4.6,
      reviews: 203,
      description: 'Durable backpack with laptop compartment.',
      features: ['Laptop Compartment', 'Water Resistant', 'Multiple Pockets'],
      inStock: true,
      isNew: true,
      isFeatured: false
    }
  ];

  const categories = [
    { id: 'all', name: 'All Products', count: mockProducts.length },
    { id: 'electronics', name: 'Electronics', count: mockProducts.filter(p => p.category === 'electronics').length },
    { id: 'clothing', name: 'Clothing', count: mockProducts.filter(p => p.category === 'clothing').length },
    { id: 'footwear', name: 'Footwear', count: mockProducts.filter(p => p.category === 'footwear').length },
    { id: 'home', name: 'Home & Kitchen', count: mockProducts.filter(p => p.category === 'home').length },
    { id: 'accessories', name: 'Accessories', count: mockProducts.filter(p => p.category === 'accessories').length }
  ];

  const sortOptions = [
    { value: 'featured', label: 'Featured' },
    { value: 'price-low', label: 'Price: Low to High' },
    { value: 'price-high', label: 'Price: High to Low' },
    { value: 'rating', label: 'Highest Rated' },
    { value: 'newest', label: 'Newest' }
  ];

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      await new Promise(resolve => setTimeout(resolve, 1500));
      setProducts(mockProducts);
      setFilteredProducts(mockProducts);
      setLoading(false);
    };

    fetchProducts();
  }, []);

  useEffect(() => {
    let filtered = [...products];

    // Filter by category
    if (selectedCategory !== 'all') {
      filtered = filtered.filter(product => product.category === selectedCategory);
    }

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter(product =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        product.category.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Filter by price range
    filtered = filtered.filter(product => 
      product.price >= priceRange[0] && product.price <= priceRange[1]
    );

    // Sort products
    switch (sortBy) {
      case 'price-low':
        filtered.sort((a, b) => a.price - b.price);
        break;
      case 'price-high':
        filtered.sort((a, b) => b.price - a.price);
        break;
      case 'rating':
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case 'newest':
        filtered.sort((a, b) => (b.isNew ? 1 : 0) - (a.isNew ? 1 : 0));
        break;
      default:
        // Featured - keep original order or sort by featured status
        filtered.sort((a, b) => (b.isFeatured ? 1 : 0) - (a.isFeatured ? 1 : 0));
    }

    setFilteredProducts(filtered);
  }, [selectedCategory, searchQuery, products, sortBy, priceRange]);

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <div className="home-page">
      <div className="container">
        <div className="page-header">
          <h1>Discover Amazing Products</h1>
          <p>Shop the latest trends and best deals in our curated collection</p>
        </div>

        {/* Enhanced Filters Section */}
        <div className="filters-section">
          <div className="filters-header">
            <h2>Filter & Sort</h2>
            <div className="results-count">
              {filteredProducts.length} products found
            </div>
          </div>

          <div className="filters-grid">
            {/* Category Filter */}
            <div className="filter-group">
              <label>Categories</label>
              <div className="category-filters">
                {categories.map(category => (
                  <button
                    key={category.id}
                    onClick={() => setSelectedCategory(category.id)}
                    className={`category-filter ${selectedCategory === category.id ? 'active' : ''}`}
                  >
                    <span className="category-name">{category.name}</span>
                    <span className="category-count">({category.count})</span>
                  </button>
                ))}
              </div>
            </div>

            {/* Price Range Filter */}
            <div className="filter-group">
              <label>Price Range: ${priceRange[0]} - ${priceRange[1]}</label>
              <div className="price-slider">
                <input
                  type="range"
                  min="0"
                  max="1000"
                  value={priceRange[0]}
                  onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])}
                  className="slider"
                />
                <input
                  type="range"
                  min="0"
                  max="1000"
                  value={priceRange[1]}
                  onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                  className="slider"
                />
              </div>
            </div>

            {/* Sort Options */}
            <div className="filter-group">
              <label>Sort By</label>
              <select 
                value={sortBy} 
                onChange={(e) => setSortBy(e.target.value)}
                className="sort-select"
              >
                {sortOptions.map(option => (
                  <option key={option.value} value={option.value}>
                    {option.label}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Products Grid */}
        {filteredProducts.length > 0 ? (
          <div className="products-grid">
            {filteredProducts.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        ) : (
          <div className="no-products">
            <div className="no-products-icon">🔍</div>
            <h3>No products found</h3>
            <p>Try adjusting your search criteria or filters</p>
            <button 
              onClick={() => {
                setSelectedCategory('all');
                setPriceRange([0, 1000]);
                setSortBy('featured');
              }}
              className="reset-filters-btn"
            >
              Reset All Filters
            </button>
          </div>
        )}

        {/* Featured Brands Section */}
        <section className="featured-brands">
          <h2>Trusted Brands</h2>
          <div className="brands-grid">
            {['Samsung', 'Apple', 'Nike', 'Sony', 'Adidas', 'Bose'].map(brand => (
              <div key={brand} className="brand-logo">
                {brand}
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
